	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			$(".gb-global-top-three, .gb-global-bottom-three").appendTo("#gb-global-icons");
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			$(".gb-global-top-three, .gb-global-bottom-three").appendTo("#gb-channel-list");
			if(!$(".hp #hp-container-four-clone").length) {
				var container4Clone = $("#sw-content-container4.ui-hp").children().clone();
				$("#hp-content").prepend('
' + container4Clone.html() + '');
			}
			$(".hp .view-calendar-link span").text("View All");
			//$(".gb-global-top-three, .gb-global-bottom-three").appendTo("#gb-global-icons");
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			$(".gb-global-top-three, .gb-global-bottom-three").appendTo("#gb-channel-list");
			if(!$(".hp #hp-container-four-clone").length) {
				var container4Clone = $("#sw-content-container4.ui-hp").children().clone();
				$("#hp-content").prepend('
' + container4Clone.html() + '');
			}
			$(".hp .view-calendar-link span").text("View All");
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			$(".gb-global-top-three, .gb-global-bottom-three").appendTo("#gb-channel-list");
			if(!$(".hp #hp-container-four-clone").length) {
				var container4Clone = $("#sw-content-container4.ui-hp").children().clone();
				$("#hp-content").prepend('
' + container4Clone.html() + '');
			}
			$(".hp .view-calendar-link span").text("View All");
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			$(".gb-global-top-three, .gb-global-bottom-three").appendTo("#gb-channel-list");
			if(!$(".hp #hp-container-four-clone").length) {
				var container4Clone = $("#sw-content-container4.ui-hp").children().clone();
				$("#hp-content").prepend('
' + container4Clone.html() + '');
			}
			$(".hp .view-calendar-link span").text("View All");
		}
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
    	//SITE MAP PAGE - Adam - 11.30.17 - #02991841
        $("#sw-sitemap-container").prepend("


Site Map");

		// ********** MYSTART ********** //
		//DOC add district home to school dropdown
		$("
ACS Home").prependTo(".sw-mystart-dropdown.schoollist .sw-dropdown-list");

		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
		$(".sw-mystart-dropdown.schoollist .selector").html("Our Schools +");
		$("#sw-myaccount").text("User Options");

		// MYSTART DROPDOWN ANIMATION
		$(".sw-mystart-dropdown").unbind();
		$(".sw-mystart-dropdown").hover(function(e) {
			e.preventDefault();
			$(".sw-dropdown").stop(true, true);
			$(".sw-dropdown", this).slideDown(250, "swing");
			$(this).addClass("open");
		}, function() {
			$(".sw-dropdown",this).slideUp(250, "swing");
			$(this).removeClass("open");
		});


		$('.sw-mystart-dropdown').hover(function () {
		    	$('.selector', this).addClass('hover2');
		}, function () {
		    	$('.selector', this).removeClass('hover2');
		});
		$('#sw-mystart-account').hover(function () {
			$(this).addClass("clicked-state");
			$('#sw-myaccount', this).addClass('hover3');
		}, function () {
			$(this).removeClass("clicked-state");
		    	$('#sw-myaccount', this).removeClass('hover3');
		});

		//DOC spaces in search bar fix
