	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $(".gb-header-right input#swsitesearch-txt-searchstring").removeAttr("placeholder");
		$(".gb-header-right input#swsitesearch-txt-searchstring").attr("value", "Search ACS");
		$(".gb-header-right input#swsitesearch-txt-searchstring").focus(function() {
			if($(this).val() == "Search ACS") {
				$(this).val("");
			}
		});
		$(".gb-header-right input#swsitesearch-txt-searchstring").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search ACS");
			}
		});
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function csOpenPageSubMenu(li) {
            if (li.prop('tagName').toLowerCase() == "li") {
                if (li.hasClass('expandable')) {
                    li.removeClass('expandable').addClass('collapsible');
                }
                if (li.find('div:first').hasClass('expandable')) {
                    li.find('div:first').removeClass('expandable').addClass('collapsible');
                }
                li.find('ul:first').slideDown(250, "swing");

                PageNavigationStateCookie();
            }
        }

        function csClosePageSubMenu(li) {
            if (li.prop('tagName').toLowerCase() == "li") {
                li.removeClass('collapsible').addClass('expandable');
                li.find('div:first').removeClass('collapsible').addClass('expandable');
                li.find('ul:first').slideUp(250, "swing");

                PageNavigationStateCookie();
            }
        }	

	function hpBgVideo() {
	        if($("#player").length) {

            	// GET CANVAS LOADER
                var canvas = document.getElementById("circleLoader");

                //IF IE
                if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
                var ctx = canvas.getContext("2d");

            	var provider = "Eduvision";

                if(provider == "Vimeo;Eduvision" || provider == "Vimeo") {

                	$("#player").html('');
                	$("#mobile-video-overlay").prepend('');

                    // VIMEO API - https://developer.vimeo.com/player/js-api | https://player.vimeo.com/playground
                    // CUSTOMIZE EMBED - https://vimeo.com/blog/post/customize-the-embedded-vimeo-player

                    var iframe = $("#vimeoBgVideo")[0];
                    var player = $f(iframe);
                    var videoStarted = false;

                    // VIMEO PLAYER IS READY
                    player.addEvent("ready", function() {
                        // MUTE THE VIDEO
                        player.api("setVolume", 0);

                        // LOOP THE VIDEO
                        if(!$(".ie9").length) {
                            player.api("setLoop", true);
                        }

                        // SHOW THE PLAY/PAUSE BUTTON WHEN VIDEO STARTS
                        if(!videoStarted) {
                            $(".circle-timer").show();

                            videoStarted = true;
                        }

                        // SET PLAYPROGRESS EVENT TO CONSTANTLY UPDATE/REDRAW THE CANVAS LOADER
                        player.addEvent("playProgress", function(data) {

                            // GET THE NEW END POINT
                            var end = (360 * data.percent).toFixed(2);

                            // REDRAW THE CANVAS LOADER
                            ctx.clearRect(0, 0, 70, 70);
                            ctx.beginPath();
                            ctx.arc(35, 35, 35, degreesToRadians(0) - (Math.PI / 2), degreesToRadians(end) - (Math.PI / 2), false);
                            ctx.lineWidth = 5;
                            ctx.strokeStyle = "#FFFFFF";
                            ctx.stroke();
                        });

                        // BIND VIDEO EVENTS
                        $(".play-pause").click(function() {
                            // PAUSE
                            if($(this).hasClass("pause")) {
                                player.api("pause");
                                $(this).removeClass("pause").addClass("play");
                            }

                            // PLAY
                            else {
                                player.api("play");
                                $(this).removeClass("play").addClass("pause");
                            }

                        });
                    });


                    // MOBILE POP UP VIDEO
                    var iframe2 = $("#mobileVideo")[0];
                    var player2 = $f(iframe2);

                    // VIMEO PLAYER IS READY
                    player2.addEvent("ready", function() {
                        // CLICK EVENT TO MOBILE VIDEO PLAY BUTTON
                        $("#mobile-play").click(function() {
                            // SHOW THE VIDEO POP UP
                            $("#mobile-video-overlay-outer").show();

                            // MAKE SURE DIMENSIONS ARE CORRECT
                            mobileVideoResize();

                            // AUTO PLAY THE VIDEO
                            player2.api("play");
                        });

                        // CLICK EVENT TO MOBILE VIDEO CLOSE BUTTON
                        $("#mobile-video-close").click(function(e) {
                            e.preventDefault();

                            // HIDE THE VIDEO POP UP
                            $("#mobile-video-overlay-outer").hide();

                            // AUTO STOP THE VIDEO
                            player2.api("pause");
                        });
                    });

                } else {

                    $("#player").html('');
                    $("#mobile-video-overlay").prepend('');

                }      
		}
	}

    function eduvisionVideo() {
    	// GET CANVAS LOADER
        var canvas = document.getElementById("circleLoader");

        //IF IE
        if (typeof(G_vmlCanvasManager) != "undefined"){G_vmlCanvasManager.initElement(canvas);}
        var ctx = canvas.getContext("2d");

    	var eduVisionPlayer1 = evplayerhadnler.init({
            iframeId: "vimeoBgVideo",
            onTime: function(data) {
            	// GET THE NEW END POINT
                var end = (360 * (data.percent / 100)).toFixed(2);

                // REDRAW THE CANVAS LOADER
                ctx.clearRect(0, 0, 70, 70);
                ctx.beginPath();
                ctx.arc(35, 35, 35, degreesToRadians(0) - (Math.PI / 2), degreesToRadians(end) - (Math.PI / 2), false);
                ctx.lineWidth = 5;
                ctx.strokeStyle = "#FFFFFF";
                ctx.stroke();	
            },
            onComplete: function(data) {}
        });

        $(".circle-timer").show();

        // BIND VIDEO EVENTS
        $(".play-pause").click(function() {
            // PAUSE
            if($(this).hasClass("pause")) {
                eduVisionPlayer1.pause();
                $(this).removeClass("pause").addClass("play");
            }

            // PLAY
            else {
                eduVisionPlayer1.play();
                $(this).removeClass("play").addClass("pause");
            }

        });

        // CLICK EVENT TO MOBILE VIDEO PLAY BUTTON
        $("#mobile-play").click(function() {
            // SHOW THE VIDEO POP UP
            $("#mobile-video-overlay-outer").show();

            // MAKE SURE DIMENSIONS ARE CORRECT
            mobileVideoResize();
        });

        // CLICK EVENT TO MOBILE VIDEO CLOSE BUTTON
        $("#mobile-video-close").click(function(e) {
            e.preventDefault();

            // HIDE THE VIDEO POP UP
            $("#mobile-video-overlay-outer").hide();
        });
    }

	function degreesToRadians(degrees) {
		return degrees * (Math.PI/180);     
	}

	function csVideoResize() {
		if($("#player").length) {
			var getWidth = $(window).width();
			if (getWidth >= 1024) { // 960+
				var topOffSet = $("#hp-video").offset().top;
				var screenWidth = $(window).width();
				var screenHeight = $(window).height() - 50 - topOffSet;
				var screenRatio = screenHeight / screenWidth;
			       	var containerHeight = screenWidth * screenRatio;
				var vidWidth = screenWidth, vidHeight = 0;

				// GET IMAGE RATIO
				var initW = 853.5, initH = 480;
				var ratio = initH / initW;
				vidHeight = vidWidth * ratio;
				if(vidHeight < screenHeight){
					vidHeight = screenHeight;
					vidWidth = vidHeight / ratio;
				}
		                var centerWidth = (vidWidth - screenWidth) / 2;
		                var centerHeight = (vidHeight - screenHeight) / 2;

				// APPLY THE DIMENSIONS
				$("#hp-video").css({"height": containerHeight + "px"});
				$("#vimeoBgVideo").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
			} if (getWidth >= 480 && getWidth < 1024) { // 768 - 480
				// GET IMAGE RATIO
				var initW = 1450, initH = 1014;
				var ratio = initH / initW;
				var vidWidth = $("#hp-video").width();
				vidHeight = vidWidth * ratio;

				if(vidHeight < 445){
					vidHeight = 445;
				}

				// APPLY THE DIMENSIONS
				$("#hp-video").css({"height": vidHeight + "px"});
				$("#vimeoBgVideo").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
			} if (getWidth < 480) { // 320
				// GET IMAGE RATIO
				var initW = 1450, initH = 1014;
				var ratio = initH / initW;
				var vidWidth = $("#hp-video").width();
				vidHeight = vidWidth * ratio;

				if(vidHeight < 300){
					vidHeight = 300;
				}

				// APPLY THE DIMENSIONS
				$("#hp-video").css({"height": vidHeight + "px"});
				$("#vimeoBgVideo").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
			}
		}
	}

	function mobileVideoResize() {
		if($("#player").length) {
			$("#mobile-video-overlay iframe").css({"width": "auto", "height": "auto"});
			var screenWidth = $(window).width();
			var screenHeight = $(window).height();
			var initVidW = 800, initVidH = 600;
			var vidRatio = initVidH / initVidW;
			vidWidth = $("#mobile-video-overlay").width();
			vidHeight = vidWidth * vidRatio;

			if((vidHeight + 100) > screenHeight) {
				vidHeight = screenHeight - 80;
			}

			$("#mobile-video-overlay").css({"height": (vidHeight + 40)});
			$("#mobile-video-overlay iframe").css({"width": vidWidth, "height": vidHeight});
		}
	}

