    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false,
        "VideoToggle": false,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.JsMediaQueries();
            this.MyStart();
            this.Translate();
            this.Header();
            this.ChannelBar();
            this.HeadlinesScroller();
            this.GlobalIcons();
            this.Body();
            this.MovePasskeys();
            this.ModEvents();
            this.FeaturedHeadlines();
            this.Footer();
            this.SocialMedia();
            this.RsMenu();
            this.AppAccordion();
            if($(".hp").length && this.VideoToggle == true) {
            	this.StreamingVideo();
            } else {
            	this.StaticImage();
            }
            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.Search();
            $(window).resize(function(){ 
            	this.HeadlinesScrollerResize(); 
            });
        },

        "WindowScroll": function() {
        	if($(window).width() > 1023) {
                var scrollTop = $(window).scrollTop();
                var videoHeight = $("#hp-video-outer").height() / 2;
                var topHeight = $("#gb-page").height() / 3;

                if($(".hp").length && scrollTop > videoHeight) {
					$("#gb-page").addClass("cs-switch-dropdowns");
                } else {
                	$("#gb-page").removeClass("cs-switch-dropdowns");
                }

                if(scrollTop > topHeight) {
                	$("#hp-scroll-up-btn").addClass("show");
                } else {
                	$("#hp-scroll-up-btn").removeClass("show");
                }
            }
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.MovePasskeys();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
					$("#hp-scroll-down-outer").before($("#cs-video-btns-outer"));

                    if(!$(".hp-content.two .hp-column.three #sw-content-container6").length) {
                    	$(".hp-content.two .hp-column.one #sw-content-container6").appendTo(".hp-content.two .hp-column.three");
                        $(".hp-content.two .hp-column.three").show();
                    }
                break;
                case "768":
					$("#hp-scroll-down-outer").before($("#cs-video-btns-outer"));

                    if(!$(".hp-content.two .hp-column.one #sw-content-container6").length) {
                    	$(".hp-content.two .hp-column.three #sw-content-container6").appendTo(".hp-content.two .hp-column.one");
                        $(".hp-content.two .hp-column.three").hide();
                    }
                break;
                case "640":
                    $("#cs-custom-video").after($("#cs-video-btns-outer"));

                    if(!$(".hp-content.two .hp-column.one #sw-content-container6").length) {
                    	$(".hp-content.two .hp-column.three #sw-content-container6").appendTo(".hp-content.two .hp-column.one");
                        $(".hp-content.two .hp-column.three").hide();
                    }
                break;
                case "480":
					$("#cs-custom-video").after($("#cs-video-btns-outer"));

                    if(!$(".hp-content.two .hp-column.three #sw-content-container6").length) {
                    	$(".hp-content.two .hp-column.one #sw-content-container6").appendTo(".hp-content.two .hp-column.three");
                        $(".hp-content.two .hp-column.three").show();
                    }
                break;
                case "320":
					$("#cs-custom-video").after($("#cs-video-btns-outer"));

                    if(!$(".hp-content.two .hp-column.three #sw-content-container6").length) {
                    	$(".hp-content.two .hp-column.one #sw-content-container6").appendTo(".hp-content.two .hp-column.three");
                        $(".hp-content.two .hp-column.three").show();
                    }
                break;
            }
        },

        "MyStart": function() {
        	// FOR SCOPE
            var _this = this;

        	// ADD DISTRICT HOME
            $(".sw-mystart-button.home").appendTo(".gb-mystart.one");

            // ADD SEARCH
            $("#sw-mystart-search").unbind();

            $("#sw-search-button").attr({
                "role": "button",
                "aria-label": "Search Our Site"
            });
            $("#sw-search-button").keydown(function(e) {
                if(e.keyCode == CreativeTemplate.KeyCodes.enter || e.keyCode == CreativeTemplate.KeyCodes.space) {
                    e.preventDefault();
                    SWGoToSearchResultsPageswsearchinput();
                }
            });
            $("#sw-search-button span").addClass("chandlerUSD-icon-font").html("");
            $("#sw-mystart-search").appendTo(".gb-mystart.two");

        	// ADD SCHOOL LIST
			if($("#sw-mystart-outer .sw-mystart-dropdown.schoollist").length) {
				var schoolDropdown =	'
' +
											'
Our Schools' +
											'
' +
												'' + $(".sw-mystart-dropdown.schoollist .sw-dropdown-list").html() + '' +
											'' +
										'';

				// ADD SCHOOL LIST TO THE DOM	
				$(".gb-mystart.one").prepend(schoolDropdown);

				// BIND DROPDOWN EVENTS
				this.DropdownActions({
					"dropdownParent": ".cs-mystart-dropdown.schools",
					"dropdownSelector": ".cs-selector",
					"dropdown": ".cs-dropdown",
					"dropdownList": ".cs-dropdown-list"
				});
			}


            	// MOVE SIGN IN BUTTON
            	$(".sw-mystart-button.signin").prependTo(".gb-mystart.three");
