        "Body": function() {
        	// FOR SCOPE
            var _this = this;

            // SCROLL DOWN BUTTON
            if($(".hp").length) {
                $(document).on("click keydown", "#hp-scroll-btn", function(e) {
                    if(_this.A11yClick(e)) {
                        e.preventDefault();

                        var scrollPosition = 0;

                        scrollPosition = $("#hp-content-outer").offset().top;

                        $("html, body").animate({
                            scrollTop: scrollPosition
                        }, "slow");
                    }
                });
            }

            // SCROLL UP BUTTON
            $(document).on("click keydown", "#hp-scroll-up-btn", function(e) {
                if(_this.A11yClick(e)) {
                    e.preventDefault();
                    $("html, body").animate({ scrollTop: 0 }, "slow", function() { });
                }
            });

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // ADJUST MORE LINKS
            $("a.more-link span").text("view all articles");
            $("#hp-scrolling-headlines .headlines a.more-link span").text("view all news");
            $(".hp-content.three .headlines a.more-link").html("view allarticles");

            // ADJUST RSS BUTTON
            $(".app-level-social-rss a.ui-btn-toolbar.rss span").text("subscribe");
            $(".ui-widget.app").each(function() {
            	var thisApp = this;
                if($(".app-level-social-rss",thisApp).length && $(".ui-read-more",thisApp).length) {
                    $(thisApp).find(".app-level-social-rss").appendTo($(thisApp).find(".ui-read-more"));
                }
            });

            // REMOVE ELEMENT SET LINKS REGION IF TOGGLED OFF
            $('.hp-content.two[data-showHeader="false"] .hp-content-header').remove();

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp-column.one .ui-widget-header h1").length) {
                $("div.sp-column.one .ui-widget-header").append("
");
            }
        },

