                if(!$(".cs-article-inner-inner",thisArticle).length) {
                    $(thisArticle).wrapInner("
");
                    $(".ui-article-title",thisArticle).prependTo(thisArticle);
                }

                if(!$(".cs-article-inner",thisArticle).length) {
                    $(thisArticle).wrapInner("
");
                    $(".ui-article-thumb",thisArticle).prependTo(thisArticle);
                }

                if($(".cs-article-inner-inner .ui-article-description",thisArticle).text() == "") {
                    $(thisArticle).addClass("cs-no-desc");
                    $(".custom-teaser-read-more",thisArticle).remove();
                }
            });

            $(".headlines.cs-desktop-headlines li .ui-article").not($(".cs-no-desc")).hover(function() {
                $(".cs-article-inner-inner",this).slideDown();
            }, function() {
                $(".cs-article-inner-inner",this).slideUp();
            });


            $(".headlines.cs-desktop-headlines li .ui-article .ui-article-title a").focus(function() {
                var thisParent = $(this).parent().parent();
                $(this).parent().parent().parent().parent().addClass("focused");
                $(".cs-article-inner-inner",thisParent).slideDown();
            });
            $(".headlines.cs-desktop-headlines li .ui-article .ui-article-title a").focusout(function() {
            	var thisParent = $(this).parent().parent();
                $(this).parent().parent().parent().parent().removeClass("focused");
                $(".cs-article-inner-inner",thisParent).slideUp();
            });
