    function LoadMinibaseList123181(PageIndex){
        var FilterFields = "";
        if ($('#sw-content-minibaselist123181-filterquery').length > 0){
        	FilterFields =encodeURIComponent($('#sw-content-minibaselist123181-filterquery').val());
        }else{
            FilterFields = escape(CreateFilterQuery123181());
        }
        var DisplayType = $("#sw-content-minibaselist123181-displaytype").val();
        if (PageIndex === undefined) {
            PageIndex = $("#sw-content-minibaselist123181-pageindex").val();
        }
        var success = '$("#sw-content-minibaselist123181-pageindex").val(' + PageIndex + ');';
        
        if (0 == 1)
        { window.open( "https://www.cusd80.com//site/Default.aspx?DomainID=0&PageID=0&PageType=22&ModuleInstanceID=110338&PageModuleInstanceID=123181&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex);
        }
        else 
        { GetContent("https://www.cusd80.com//site/UserControls/Minibase/MinibaseListWrapper.aspx?ModuleInstanceID=110338&PageModuleInstanceID=123181&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex, "sw-content-minibaselist123181", 2, success);
        }
    
    }
    function LoadMinibaseFilter123181(){
        GetContent("https://www.cusd80.com//site/UserControls/Minibase/MinibaseFilterWrapper.aspx?ModuleInstanceID=110338&PageModuleInstanceID=123181", "sw-content-minibaselist123181", 2);
    }
    function CreateFilterQuery123181(){
        var filterquery = "";
        $.each($('#sw-content-minibasefilter123181 label'), function(i){
            var id = $(this).attr("for");   
            if($('#' + id).val() != ''){
                var fieldindex = id.substring(id.lastIndexOf('-') + 1);
                //filterquery += $(this).text() 
                
                var comparetype = "E";
                if ($('#sw-content-minibasefilter-field123181' + fieldindex + '-comparetype').val() !== undefined) {
                    comparetype = $('#sw-content-minibasefilter-field123181' + fieldindex + '-comparetype').val();
                }
                filterquery += fieldindex + ":" + comparetype + ":";
                if (comparetype != "E") { 
                    filterquery += $('#' + id).val().replace(/%/g, '[%]').replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                } else {
                    filterquery += $('#' + id).val().replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                }
            }            
            
            /*if(i < ($('#sw-content-minibasefilter110338 label').length - 1)) {
                filterquery += ";";
            }*/
        });
        filterquery += "";
        return filterquery;
    }

