    function LoadMinibaseList637235074504663995(PageIndex){
        var FilterFields = "";
        if ($('#sw-content-minibaselist637235074504663995-filterquery').length > 0){
        	FilterFields =encodeURIComponent($('#sw-content-minibaselist637235074504663995-filterquery').val());
        }else{
            FilterFields = escape(CreateFilterQuery637235074504663995());
        }
        var DisplayType = $("#sw-content-minibaselist637235074504663995-displaytype").val();
        if (PageIndex === undefined) {
            PageIndex = $("#sw-content-minibaselist637235074504663995-pageindex").val();
        }
        var success = '$("#sw-content-minibaselist637235074504663995-pageindex").val(' + PageIndex + ');';
        
        if (0 == 1)
        { window.open( "https://www.cusd80.com//site/Default.aspx?DomainID=0&PageID=0&PageType=22&ModuleInstanceID=120766&PageModuleInstanceID=637235074504663995&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex);
        }
        else 
        { GetContent("https://www.cusd80.com//site/UserControls/Minibase/MinibaseListWrapper.aspx?ModuleInstanceID=120766&PageModuleInstanceID=637235074504663995&FilterFields=" + FilterFields + "&DirectoryType=" + DisplayType + "&PageIndex=" + PageIndex, "sw-content-minibaselist637235074504663995", 2, success);
        }
    
