    function LoadMinibaseFilter637235074504663995(){
        GetContent("https://www.cusd80.com//site/UserControls/Minibase/MinibaseFilterWrapper.aspx?ModuleInstanceID=120766&PageModuleInstanceID=637235074504663995", "sw-content-minibaselist637235074504663995", 2);
    }
    function CreateFilterQuery637235074504663995(){
        var filterquery = "";
        $.each($('#sw-content-minibasefilter637235074504663995 label'), function(i){
            var id = $(this).attr("for");   
            if($('#' + id).val() != ''){
                var fieldindex = id.substring(id.lastIndexOf('-') + 1);
                //filterquery += $(this).text() 
                
                var comparetype = "E";
                if ($('#sw-content-minibasefilter-field637235074504663995' + fieldindex + '-comparetype').val() !== undefined) {
                    comparetype = $('#sw-content-minibasefilter-field637235074504663995' + fieldindex + '-comparetype').val();
                }
                filterquery += fieldindex + ":" + comparetype + ":";
                if (comparetype != "E") { 
                    filterquery += $('#' + id).val().replace(/%/g, '[%]').replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                } else {
                    filterquery += $('#' + id).val().replace(/:/g, '[$C0l0n$]').replace(/;/g, '[$Semic0l0n$]') + ";";
                }
            }            
            
            /*if(i < ($('#sw-content-minibasefilter120766 label').length - 1)) {
                filterquery += ";";
            }*/
        });
        filterquery += "";
        return filterquery;
    }

