(function($) {
    "use strict";

    $(document).ready(function() {
        var carouselId = 'stm-posts_carousel-5ea5d1a975e12';
        var $carousel = $('#'+carouselId);

        $('#'+carouselId).owlCarousel({
            loop   : false,
            margin : 30,
            nav    : false,
            lazyLoad:true,
            responsive:{
                0 : {
                    items : 1
                },
                640 : {
                    items : 2
                },
                992 : {
                    items : 3
                }
            }
        });

        $('.vc_tta-tab').click(function(){
            setTimeout(function(){
                $carousel.trigger('destroy.owl.carousel');
                $carousel.html($carousel.find('.owl-stage-outer').html()).removeClass('owl-loaded');

                $('#'+carouselId).owlCarousel({
                    loop   : false,
                    margin : 30,
                    nav    : false,
                    lazyLoad:true,
                    responsive:{
                        0 : {
                            items : 1
                        },
                        640 : {
                            items : 2
                        },
                        992 : {
                            items : 3
                        }
                    }
                });
            }, 300);
        });

    });

})(jQuery);

