div.script:
	jQuery(document).ready(function($){
		var eventList;
		var $eventsPanel = jQuery('.events__panel');
		var eventCat = $eventsPanel.attr('data-event-cat');
		var eventsUrl = $eventsPanel.attr('data-events-url');
		var async = jQuery.ajax({
			url: eventsUrl 
				+ '?view=upcoming'
				+ '&evt_cat%5B%5D=' + eventCat
				+ '&lang=' + jb_lang_strings['lang']
				+ '&format=json'
				+ '&limit=4',
			dataType: 'jsonp'
		});
		async.done(function(data){
			eventList = data.output;
			if(eventList.indexOf('no-events clearfix') > -1){
				noEventsFound();
				return;
			}
			$eventsPanel.html(eventList);
		});
		async.fail(function(){
			noEventsFound();
		});

		var noEventsFound = function(){
			$eventsPanel.html('
' + jb_lang_strings['eventsNotFound'] + '');
		}

		// Enable clicking an event to view it on the calendar
		$eventsPanel.on('click', '.event', function(){
			var $this = jQuery(this);
			window.location = $this.attr('data-calendar-url');
		});

		$eventsPanel.on('keydown', '.event', function(e) {
			var $this = jQuery(this);
			var key = e.which;
			if(key == 13)  // the enter key code
			{
				jQuery($this).click();
				return false;
			}
		});

		$('.slide-prev, .slide-next').on('keydown', function(e) {
			var $this = jQuery(this);
			var key = e.which;
			if(key == 13)  // the enter key code
			{
				jQuery($this).click();
				return false;
			}
		});

	});
