$(function () {
    var $floatColumn = $("div#ColumnUserControl7.home_floatarea");
    // here if the custom content for "Important Notice" only has some "p" "br", but has no words, the column will still be displayed
    // because the custom content is not really empty; and when user logged in CMS6, when edit page/page template, the $floatColumn will
    // have some elements such as widget wrapper or something else, so $floatColumn.html() will always have contents, so it will always be 
    // displayed at backend, but when user logged 
    // out and the frontend page will not have other elements but only the custom content, so when the custom content is really empty, we need
    // to hide the column
    if ($.trim($floatColumn.html()) === '') {
        $floatColumn.hide();
    }
});

