div.script:
$(document).ready(function () {
    $("#66207fd4-1212-4306-afea-83dc2f3ee9e9").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 7 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#050983f3-ce36-44d1-9a11-e5d287161a42").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#050983f3-ce36-44d1-9a11-e5d287161a42').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#050983f3-ce36-44d1-9a11-e5d287161a42').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 8 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#018c6599-4e33-4cc3-992e-72cf711fd9b8').hide();
    jQuery('#018c6599-4e33-4cc3-992e-72cf711fd9b8').listSolrResults({
        currentPagePath: '/content/schools/arva/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '018c6599-4e33-4cc3-992e-72cf711fd9b8',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

