    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "IconList": "",

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.MyStart();
            this.Header();
            this.ChannelBar();
            this.Dropdown();
            this.Body();
            this.Slideshow();
            this.GlobalIcons(_this);
            this.RsMenu(_this);
            this.Footer();
            this.WindowResize();
            this.Scrolling();

            $(window).scroll(function(){ _this.Scrolling(); });
            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.CalendarListView();

            if($('#ui-btn-mypasskey').length) {

            	$('#ui-btn-mypasskey').addClass('gb-mystart-link-alt passkey cs-fc cs-aic');
            	$('#gb-header-right').append($('#ui-btn-mypasskey'));
                $('#ui-btn-mypasskey').text('PASSKEYS');

            }

            $('#sw-mystart-outer').remove();

            // MAKE NAVIGATION APPS ACCESSIBLE
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();

        },

        "WindowResize": function() {
            this.CalendarListView();

            var wW = $(window).width();

            if(wW < 1024) {
            	$('#gb-channel-list').removeClass('cs-jc').addClass('cs-jfe');
            	$('#hp-slideshow').prepend($('#gb-global-icons'));
                $('#gb-global-icons').removeClass('cs-fw');
                $('#hp-slideshow .rs-gallery-container').addClass('cs-fw');
            	$('#gb-footer-links').removeClass('cs-jfe').addClass('cs-jfs');
                $('#hp-slideshow #hp-gallery-controls').height($('#hp-slideshow .rs-gallery-images').height());
                $('#gb-schoolwires-footer').append($('#gb-sitemap-wrapper'));
                $('#gb-header-left').removeClass('cs-fw');
                if(wW < 640) {
                	$('#gb-schoolwires-footer').prepend($('#gb-sitemap-wrapper'));
                    if(wW < 480) {
                    	$('#gb-header-left').addClass('cs-fw');
                    }
                }
            } else {
            	$('#gb-channel-list').addClass('cs-jc').removeClass('cs-jfe');
            	$('#hp-slideshow .rs-gallery-container').append($('#gb-global-icons'));
                $('#gb-global-icons').addClass('cs-fw');
                $('#hp-slideshow .rs-gallery-container').removeClass('cs-fw');
            	$('#gb-footer-links').addClass('cs-jfe').removeClass('cs-jfs');
                $('#hp-slideshow #hp-gallery-controls').height('100%');
                $('#gb-schoolwires-footer').append($('#gb-sitemap-wrapper'));
                $('#gb-header-left').removeClass('cs-fw');
            }

        },

        "Scrolling": function() {

        	var wScroll = $(window).scrollTop();
            var nav = $('nav').position();
            var navH = $('nav').height();

            if(wScroll >= nav.top) {
            	$('#gb-channel-list-outer').addClass('fixed');
                $('nav').height(navH + 'px');
            } else {
            	$('#gb-channel-list-outer').removeClass('fixed');
                $('nav').height('auto');
            }

        },

        "MyStart": function() {
        	// ADD TRANSLATE
        	$("#gb-header-right #gb-translate-dropdown").creativeTranslate({
                "type": 1,

                "advancedOptions": {
                    "addMethod": "append",
                    "dropdownHandleText": "Translate",
                    "customElement": { // ONLY FOR FRAMESET AND API VERSIONS
                        "useCustomElement": true,
                        "translateItemsList": true, // true = THE TRANSLATE ITEMS WILL BE AN UNORDERED LIST, false = THE TRANSLATE ITEMS WILL JUST BE A COLLECTION OF  TAGS
                        "customElementMarkup": "[$CreativeTranslateListItems$]" // CUSTOM HTML MARKUP THAT MAKES THE CUSTOM TRANSLATE ELEMENT/STRUCTURE - USE [$CreativeTranslateListItems$] ACTIVE BLOCK IN THE MARKUP WHERE THE TRANSLATE ITEMS SHOULD BE ADDED
                	}
                }
            });

            $(document).off("click", ".sw-mystart-dropdown");
            $(document).on("click", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing");
                $(this).addClass("open");
            });
            $(".sw-mystart-dropdown").hover(function(e) {
                e.preventDefault();
            }, function() {
                $(".sw-dropdown",this).slideUp(300, "swing").attr('aria-hidden', 'true');
                $(this).removeClass("open");
            });
            $(document).off("focus", ".sw-mystart-dropdown");
            $(document).on("focus", ".sw-mystart-dropdown", function(e) {
                $(".sw-dropdown", this).slideDown(300, "swing").attr('aria-hidden', 'false');
                $(this).addClass("open");
            });



            var signinSVG = '';

            if($('#ui-btn-signin').length) {
            	$('#ui-btn-signin a').addClass('gb-mystart-link signin cs-fc cs-aic accent-1');
            	$('#ui-btn-signin a').insertBefore($('#gb-transalte'));
                $('.gb-mystart-link.signin').prepend(signinSVG);
            }



            //--> Add SLM
            $.schoolListMinibase({
                'miniBaseDomainID': 4,
                'miniBaseModuleInstanceID': 3776,
                'cache': true,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '
District Home'; //Adam - 11.1.17 - 02969624

                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    $('.gb-school-list').html(schools);
                }
            });

            //--> Setup Search
            $('#sw-search-input-label').prepend($('#sw-mystart-search script'), $('#sw-search-input'));
            $('#sw-search-input').attr('aria-hidden', 'true').attr('tabindex', '-1');
            $('#sw-search-input').attr('placeholder', 'Searching for...').attr('aria-hidden', 'true');


            //--> Search Button
            $('#gb-search-btn').unbind('click').click(function(e){

            	e.preventDefault();

                var school = $('#gb-schools').position();
                var width = $('#gb-header-right').width();
                width = width - school.left - 40;

                    /*if(width > 500) {
                    	width = 500;
                    } else {
                		width = width - 40;
                    }*/

            	if($(this).hasClass('open')) {

                	$('#gb-header-right').removeClass('search-open');
                    $('#sw-channel-list-container').attr('aria-hidden', 'false');
                    $('#gb-header-right > a, #gb-header-right button').attr('tabindex', '0');
            		$('#gb-search-outer #sw-search-input').attr('aria-hidden', 'true').attr('tabindex', '-1').removeClass('open');
                    $('#gb-search-outer #sw-search-input').animate({width: 0, padding: "0px"}, 500);
                	$(this).removeClass('open').attr('aria-label', 'Open Search');

                } else {

                	$('#gb-header-right').addClass('search-open');
                    $('#gb-header-right > a, #gb-header-right button:not(".search")').attr('aria-hidden', 'true');
                    $('#gb-header-right > a, #gb-header-right button:not(".search")').attr('tabindex', '-1');
            		$('#gb-search-outer #sw-search-input').attr('aria-hidden', 'false').attr('tabindex', '0').addClass('open');
                    $('#gb-search-outer #sw-search-input').animate({width: width + 'px', padding: "0 16px"}, 500, function(){
                    	$(this).focus().attr('aria-hidden', 'false');
                    });
                	$(this).addClass('open').attr('aria-label', 'Close Search');

                }

            });
        },

