   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=m9RkPjCiwtsOaxzr%2bgbzZA%3d%3d&SessionID=f94ccbaa-60eb-4653-b76d-885780774d82&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f26%2f2020+11%3a44%3a29+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.94.249';
