$(document).ready(function() {

	/* ---	Equal height function resizeable on window resize ---- */
	if ($(window).width() > 880) {
		delay(function() {
			$('.main .col-item.same-height').css('height', 'auto'); //solve for all you browser stretchers out there!
			setEqualHeight($('.main .col-item.same-height'));
		}, 500);
	}
	$(window).resize(function() {
		if ($(window).width() > 880) {
			delay(function() {
				$('.main .col-item.same-height').css('height', 'auto'); //solve for all you browser stretchers out there!
				setEqualHeight($('.main .col-item.same-height'));
			}, 500);

		} else {
			$(".main .col-item.same-height").css('height', 'auto');
		}
	});

	$(window).on('scroll', function() {
		var delay = 0;
		$('.main .col-item').children().each(function(i) {

			var bottom_of_object = $(this).offset().top + 100;
			var bottom_of_window = $(window).scrollTop() + $(window).height();
			if (bottom_of_window > bottom_of_object) {

				var $li = $(this);
				setTimeout(function() {
					$li.addClass('active');
				}, delay += 100);
			}

		});

	});
	$(window).trigger('scroll');

	 if ($(window).width() > 980) {
  	        var bulletinBoardHeight = $('#slider .left-slider').outerHeight();
  	        $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "min-height", bulletinBoardHeight );
  	        $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "max-height", bulletinBoardHeight + 100 );
}
$(window).on('resize', function () {
  	if ($(window).width() > 980) {
		        var bulletinBoardHeight = $('#slider .left-slider').outerHeight();
		        $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "min-height", bulletinBoardHeight );
		        $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "max-height", bulletinBoardHeight + 100 );
        }
        else {
            $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "min-height", '90px' );
            $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "max-height", '450px');
        }
  });
	$('.right-slider').bxSlider({
		mode: 'fade',
		auto: true,
		autoHover: true,
		adaptiveHeight: true,
		controls: false,
		onSlideAfter: function() {
		    $('.bx-pager-link').attr('tabindex', '-1').attr('aria-hidden', 'true');
		    if ($(window).width() > 980) {
              var bulletinBoardHeight = $('#slider .left-slider').outerHeight();
              $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "min-height", bulletinBoardHeight );
              $('#slider .bx-wrapper .bx-viewport, .right-slider img').css( "max-height", bulletinBoardHeight + 100 );
            }
		}

	});


});
