    }, 500);
    $(".css-only-modal").css("opacity", "0");
    $(".css-only-modal").css("pointer-events", "none");
    $("#css-only-modals").css("opacity", "0");
    $("#css-only-modals").css("pointer-events", "none");
    $("#screen-shade").css("opacity", "0");
    $("#screen-shade").css("pointer-events", "none");
} else {
    if ($("#closings1_divAlertBanner").length > 0) {
        $("#css-only-modals").css("display", "block");
    }
}
function SetSCAASession(s) {
    localStorage.setItem("ShowClosingsAndAlerts", s);
    if (s == "true") {
        $("#css-only-modals").css("display", "block");
        $("#AlertShow").css("opacity", "0");
        $("#AlertShow").css("pointer-events", "none");
        $(".css-only-modal").css("opacity", "1");
        $(".css-only-modal").css("pointer-events", "auto");
        $("#css-only-modals").css("opacity", "1");
        $("#css-only-modals").css("pointer-events", "auto");
        $("#screen-shade").css("opacity", "0.5");
        $("#screen-shade").css("pointer-events", "auto");
    } else {
        $("#AlertShow").css("display", "block");
        $("#AlertShow").css("opacity", "1");
        $("#AlertShow").css("pointer-events", "auto");
        $(".css-only-modal").css("opacity", "0");
        $(".css-only-modal").css("pointer-events", "none");
        $("#css-only-modals").css("opacity", "0");
        $("#css-only-modals").css("pointer-events", "none");
        $("#screen-shade").css("opacity", "0");
        $("#screen-shade").css("pointer-events", "none");
    }
}

    function MobileNavSlider() {
        if ($("#mobile_top_nav").hasClass("Open") == false) {
            $("#mobile_top_nav").addClass("Open");
            $(".MobileSlider").addClass("Open");
            $("body").addClass("mobilenav_open");
        }
        else {
            $("#mobile_top_nav").removeClass("Open");
            $(".MobileSlider").removeClass("Open");
            $("body").removeClass("mobilenav_open");
        }
    }

    function setSubMenuPositions() {
        var width = $("#navigation").innerWidth() + 30;

        $("li.nav_group").each(function () {

            var offset = getOffset($(this), $("#navigation"));
            var subitem = $(this).find(".nav_megamenu");

            subitem.css("left", offset.left - 15 + "px");
            subitem.css("width", width);
        });
    };

    function getOffset(parent, child){
        var childPos = child.offset();
        var parentPos = parent.offset();
        return {
            top:  childPos.top - parentPos.top,
            left: childPos.left - parentPos.left
        };
    };

    MobileMegaMenu();

    $(window).ready(function () {
        $(window).resize(function () {
            setSubMenuPositions();
        }).resize()
    });

