    $(document).ready(function(){
        $(".LiquidButton").click(function () {
            $(this).toggleClass("LiquidButton_Open");

            // Toggle the nav 
            $(".LiquidHeader").toggleClass("Open");

            // Add the transition animation class to the body and toggle the open class
            $("body").addClass("animateliquid");
            $("body").toggleClass("liquidopen");

            // Set a timeout to remove the class once we're done animating the body opening/closing
            window.setTimeout('$("body").removeClass("animateliquid");', 500);
        });
    });

