    var countUpGrad = new CountUp("gradratecount", 0, 94.4, 1);
    var countUpLarge = new CountUp("largecount", 0, 15);
    var countUpProg = new CountUp("progcount", 0, 18);
    var countUpCareer = new CountUp("careercount", 0, 19);
    var countUpComp = new CountUp("compcount", 0, 44);
    var countUpSpecial = new CountUp("specialcount", 0, 8);




    var factsContainer = $("#facts_container");
    var started = false;

    function checkCountUp() {
        var factsOffset = factsContainer.offset().top;
        var windowHeight = $(window).scrollTop() + $(window).outerHeight() - 200;
        if (factsOffset < windowHeight && !started) {
            countUpGrad.start();
            countUpProg.start();
            countUpLarge.start();
            countUpCareer.start();
            countUpComp.start();
            countUpSpecial.start();
            started = true;
        }
    }

    $(window).scroll(function () {
        checkCountUp();
    });

    $(window).resize(function () {
        checkCountUp();
    }).resize();

