            $(document).ready(function () {
                if (!$.fn.slick) {
                    alert ("Cannot find Library to run Slider. Please enable Carousel or Slider API in [ Libraries to Use ] Tab!");
                }
                else {
                    $("#fa18479a-09e4-4fe5-a31d-ec5d55904fd1").slick({
                        slidesToShow: 1,
                        slidesToScroll: 1,
                        autoplay: true,
                        autoplaySpeed: 10000,
                        fade: true,
                        dots: false,
                        centerMode: true,
                        lazyLoad: true,
                        mobileFirst: true,
                        centerMode: true
                    });

                    setTimeout(function () {
                        $("#fa18479a-09e4-4fe5-a31d-ec5d55904fd1").css("visibility", "visible");
                    }, 500);
                }
            });

div.script:
        function ReduceFontsize() {
            var md = new MobileDetect(window.navigator.userAgent);
            if (md.phone()) {
                var titles = $(".carousel-title");
                titles.each(function () {
                    var t = $(this);
                    var fs = parseFloat(t.css("font-size"));
                    t.css("font-size", (fs * 0.8) + "px");
                });

                var descriptions = $(".carousel-description");
                descriptions.each(function () {
                    var t = $(this);
                    var fs = parseFloat(t.css("font-size"));
                    t.css("font-size", (fs * 0.95) + "px");
                });
            }
        }

        $(document).ready(function () {
            ReduceFontsize();

            /*
            setTimeout(function () {
                var msg = '

' +
