    // ------------------------------------------------
    // This code is intended to detect iphone
    // and show only Link to all Video macro
    // otherwise, display normal
    // ------------------------------------------------
    $(document).ready(function () {
        if (isiPhone() || isiPod()) {
            $(".videoPlyrAlt").css("display", "block");
            $(".videoPlyr").css("display", "none");
        }
    });
