$(".group848678").colorbox({rel:'group848678', maxWidth: '80%'});
$( window ).resize( function(){
  if( $('#cboxOverlay' ).css( 'visibility' ) ){
  $(".group848678").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
}
});
var $gallery848678 = $("a[rel=group848678]").colorbox({maxWidth: "80%"});
$("a#openGallery848678").click(function(e){
e.preventDefault();
$gallery848678.eq(0).click();
});

