$(".group857525").colorbox({rel:'group857525', maxWidth: '80%'});
$( window ).resize( function(){
if( $('#cboxOverlay' ).css( 'visibility' ) ){
$(".group857525").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
}
});
var $gallery857525 = $("a[rel=group857525]").colorbox({maxWidth: "80%"});
$("a#openGallery857525").click(function(e){
e.preventDefault();
$gallery857525.eq(0).click();
});

