div.script:
if (!('placeholder' in document.createElement('input'))) {
	var el = document.getElementById('q');
	var orig = 'Search this site';
	el.value = orig;

	el.onclick = function() {
		if (this.value==orig) this.value='';
	}

	el.onblur = function() {
		if (this.value=='') this.value=orig;
	}
}

(function($JssorSlider$) {
	'use strict';

	var maxWidthOrHeight = 
		document.getElementById('edlio_slider_container_12925').clientWidth;

	var maxNumberOfBullets = Math.round(
		maxWidthOrHeight / 35
	);
	var numberOfSlides = 13;

	var options = {
		$FillMode: 2,             
		$AutoPlay: true,          
		$LazyLoading: 1,          
		$AutoPlayInterval: 6000,  
		$SlideDuration: 500,      
		$PauseOnHover: 0,         
		$DragOrientation: 0,      
		$HWA: false,              
		$SlideshowOptions: {
			$Class: $JssorSlideshowRunner$,
			$Transitions: [
				{$Duration:700,$Opacity:2,$Brother:{$Duration:1000,$Opacity:2}}, // twin fade 

			],
			$TransitionsOrder: 0  
		},

		$ArrowNavigatorOptions: {                       
			$Class: $JssorArrowNavigator$,              
			$ChanceToShow: 2,                           
			$AutoCenter: 2,                             
			$Steps: 1                                   
		},
		$ThumbnailNavigatorOptions: {                   
			$Class: $JssorThumbnailNavigator$,          
			$ChanceToShow: 2,                           
			$ActionMode: 1,                             
			$SpacingX: 8,                               
			$DisplayPieces: 8,                          
			$ParkingPosition: 360,                      
			$Orientation: 
					1

		},
		$BulletNavigatorOptions: {                      
			$Class: $JssorBulletNavigator$,             
			$ChanceToShow: 2,                           
			$AutoCenter: 
					1
				,                                
			$Steps: 1,                                  
			$Lanes: Math.ceil(numberOfSlides / maxNumberOfBullets),              
			$SpacingX: 10,                              
			$SpacingY: 10,                              
			$Orientation: 
					1

		},
		$CaptionSliderOptions: {
			$Class: $JssorCaptionSlider$,
			$CaptionTransitions: [{$Duration:700,$Opacity:2,$Brother:{$Duration:1000,$Opacity:2}}],
			$PlayInMode: 1,
			$PlayOutMode: 3
		}

	};
	var edlioJssorSlider = new $JssorSlider$('edlio_slider_container_12925', options);


	function resizeEdlioSlider () {
		var parentWidth = document.getElementById('edlio_slider_container_12925').parentElement.clientWidth;

		if (parentWidth) {
			edlioJssorSlider.$ScaleWidth(parentWidth);
		} else {
			window.setTimeout(resizeEdlioSlider, 30);
		}
	}


		resizeEdlioSlider();







		window.addEventListener("load", resizeEdlioSlider);
		window.addEventListener("resize", resizeEdlioSlider);
		window.addEventListener("orientationchange", resizeEdlioSlider);


})($JssorSlider$);

li.script:
var vp_max = 1350,
		vp_large = 1024,
		vp_mid	= 768,
		vp_small = 320;

	$(document).ready(function(){
		$('#news_slick').slick({
			slidesToShow: 2,
			responsive: [
				{
					breakpoint: vp_max - 1,
					settings: {
						slidesToShow: 1
					}
				}
			]
		});
		$('#infographics_slick').slick({
			slidesToShow: 5,
			responsive: [
				{
					breakpoint: vp_large - 1,
					settings: {
						slidesToShow: 3
					}
				},
				{
					breakpoint: vp_mid - 1,
					settings: {
						slidesToShow: 1
					}
				}
			]
		});
	});

	window.onload = init;
	var topnavButton = document.getElementById('topnav_mobile_toggle');
	var topnavDisplay = document.getElementById('topnav');

	function init() {
		topnavButton.onclick = toggleNav;
	}

	function toggleNav(){
		topnavDisplay.classList.toggle("open");
	}

	window.addEventListener('scroll', function(event) {
    var	toggleElement = document.getElementById('header_main');
    		//watchHeight = document.getElementById('social_search_container').offsetHeight;

    if ( window.pageYOffset >= 1 ) {
			toggleElement.classList.add('collapsed-header');
      return;
    } else {
      toggleElement.classList.remove('collapsed-header');
      return;
    }
	});
