	function pgScreenSize(){
		//var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			if(!$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .more-link").length || !$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .app-level-social-rss").length) {
				$(".hp.column.one div.ui-widget.app.headlines .app-level-social-rss").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
				$(".hp.column.one div.ui-widget.app.headlines .more-link").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
			}
			if(!$("#sw-mystart-right #sw-mystart-search").length) {
				$("#sw-mystart-search").before($("#sw-mystart-right .clear"));
			}
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			if(!$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .more-link").length || !$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .app-level-social-rss").length) {
				$(".hp.column.one div.ui-widget.app.headlines .app-level-social-rss").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
				$(".hp.column.one div.ui-widget.app.headlines .more-link").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
			}
			if(!$("#sw-mystart-right #sw-mystart-search").length) {
				$("#sw-mystart-search").before($("#sw-mystart-right .clear"));
			}	
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			if(!$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .more-link").length || !$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .app-level-social-rss").length) {
				$(".hp.column.one div.ui-widget.app.headlines .app-level-social-rss").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
				$(".hp.column.one div.ui-widget.app.headlines .more-link").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
			}
			if($("#sw-mystart-right #sw-mystart-search").length) {
				$("#sw-mystart-search").prependTo($(".gb-global-icons-inner"));
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView();
			if(!$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .more-link").length || !$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .app-level-social-rss").length) {
				$(".hp.column.one div.ui-widget.app.headlines .app-level-social-rss").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
				$(".hp.column.one div.ui-widget.app.headlines .more-link").appendTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header"));
			}
			if($("#sw-mystart-right #sw-mystart-search").length) {
				$("#sw-mystart-search").prependTo($(".gb-global-icons-inner"));
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView320();
			if($(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .more-link").length || $(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .app-level-social-rss").length) {
				$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .more-link").prependTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-detail"));
				$(".hp.column.one div.ui-widget.app.headlines .ui-widget-header .app-level-social-rss").prependTo($(".hp.column.one div.ui-widget.app.headlines .ui-widget-detail"));
			}
