        }
    }
    , sendBeat: (function () {
        var beatUrl = 'https://frog.wix.com/bt?src=29&evid=3'
            + '&v=1.5728.0'
            + '&msid=9b38e1b8-1a47-4d8b-956c-7bea82568dbe'
            + '&isp=1'
            + '&st=2'
            + '&dc=42'
            + '&iss=1'
