    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.DynamicBackgrounds();
            this.Header();
            this.MyStart();
            this.SchoolList();
            this.ChannelBar();
            this.Body();
            this.UpcomingEvents();
            this.Footer();
            this.Slideshow();
            this.RsMenu();
            this.AppAccordion();
            this.Search();
            this.JsMediaQueries();

            if($(".hp").length) {
                this.WindowScroll(); 
            }

            //Variables
            var image = $.trim('/cms/lib/CA01902791/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');

            if(true) {
            	var srcCheck = image.indexOf("Faces/default-man.jpg") > -1;

                if((image != "") && (!srcCheck)) {

                } else {
                    image = "/cms/lib/CA01902791/Centricity/Template/22/gb-high-contrast-logo.png";
                }
            }

            $.csHighContrastBar({
                "show": true,
                "alternateLogo": {
                    "selector": "#gb-logo-image", // ID OR CLASS OF DEFAULT LOGO 
                    "src": image, // ALTERNATE LOGO SRC
                    "alt": "" // ALTERNATE LOGO ALT TEXT
                },
                "toggle": function(on) {
                	if(on) {
                    	$("#gb-logo .cs-contrast-logo").append
                        _this.LoadImage("#gb-footer-logo", '/cms/lib/CA01902791/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/CA01902791/Centricity/Template/22/gb-high-contrast-footer-logo.png', true);

                    } else {
                    	_this.LoadImage("#gb-footer-logo", '/cms/lib/CA01902791/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/CA01902791/Centricity/Template/22/gb-footer-logo.png', true);
                    }
                } // CUSTOM CALLBACK WHEN CONTRAST IS TOGGLED (ALSO FIRES ON DOC READY) - on PARAMETER IS EITHER true OR false (boolean)
            });

            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });
            $(window).scroll(function(){
                if($(".hp").length) {
                	_this.WindowScroll(); 
                }
            });
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {
            this.MegaMenu();

        	// Masonary
            $('.hp-row.one .ui-articles').masonry({
                // options...
                itemSelector: 'li',
                columnWidth: 'li',
                gutter: 8,
				percentPosition: true,
                horizontalOrder: true
            });
        },

        "WindowResize": function() {
            this.JsMediaQueries();
        },

        "WindowScroll": function() {
            if($(window).scrollTop() < $("#hp-slideshow-outer").height()) {
            	$("#hp-slideshow .mmg-description").addClass("animate-element");
            } else {
            	$("#hp-slideshow .mmg-description").removeClass("animate-element");
            }

            if($(window).scrollTop() > $(".hp-row.one").offset().top+$(".hp-row.two div.ui-widget.app .ui-widget-header").outerHeight(true)-100) {
            	$(".hp-row.two div.ui-widget.app .ui-widget-header, .hp-row.two .ui-articles div.ui-article").addClass("animate-element");
            } else {
            	$(".hp-row.two div.ui-widget.app .ui-widget-header, .hp-row.two .ui-articles div.ui-article").removeClass("animate-element");
            }

            if($(window).scrollTop() > $(document).height() - $(".hp-row.two").offset().top - $(".hp-row.three").height()) {
            	$(".hp-row.three div.ui-widget.app .ui-widget-header").addClass("animate-element");
            } else {
            	$(".hp-row.three div.ui-widget.app .ui-widget-header").removeClass("animate-element");
            }

            if($(window).scrollTop() > $(".hp-row.three").offset().top+$(".hp-row.four div.ui-widget.app .ui-widget-header").outerHeight(true)-250) {
            	$(".hp-row.four .ui-articles div.ui-article").addClass("animate-element");
            } else {
            	$(".hp-row.four .ui-articles div.ui-article").removeClass("animate-element");
            }
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
            		$("#sw-mystart-mypasskey").appendTo($(".gb-footer.two"));

                    $("#hp-slideshow").css("margin-bottom", -$("#gb-global-icons").height());

                    $(".hp-row div.ui-widget.app").each(function() {
                        if($(".ui-read-more", this).length) {
                            $(".ui-read-more", this).appendTo($(".ui-widget-header", this));
                        }
                        if($(".view-calendar-link", this).length) {
                            $(".view-calendar-link", this).appendTo($(".ui-widget-header", this));
                        }
                    });

                    $("#gb-social-media-icons-list").prependTo($("#gb-social-media-icons"));
                break;
                case "768":
             		$("#sw-mystart-mypasskey").appendTo($("#gb-mobile-mystart"));

                    $(".hp-row div.ui-widget.app").each(function() {
                        if($(".ui-read-more", this).length) {
                            $(".ui-read-more", this).appendTo($(".ui-widget-header", this));
                        }
                        if($(".view-calendar-link", this).length) {
                            $(".view-calendar-link", this).appendTo($(".ui-widget-header", this));
                        }
                    });

                    $("#gb-social-media-icons-list").prependTo($("#gb-social-media-icons"));
                break;
                case "640":
             		$("#sw-mystart-mypasskey").appendTo($("#gb-mobile-mystart"));

                    $(".hp-row div.ui-widget.app").each(function() {
                        if($(".ui-read-more", this).length) {
                            $(".ui-read-more", this).appendTo($(".ui-widget-footer", this));
                        }
                        if($(".view-calendar-link", this).length) {
                            $(".view-calendar-link", this).appendTo($(".ui-widget-footer", this));
                        }
                    });

                    $("#gb-social-media-icons-list").prependTo($(".gb-footer-column"));
                break;
                case "480":
                case "320":
