                            $(".ui-read-more", this).appendTo($(".ui-widget-footer", this));
                        }
                        if($(".view-calendar-link", this).length) {
                            $(".view-calendar-link", this).appendTo($(".ui-widget-footer", this));
                        }
                    });

                    $("#gb-social-media-icons-list").prependTo($(".gb-footer-column"));
                break;
            }
        },

        "DynamicBackgrounds": function() {
        	//Variables
            var contentBGSrc = $.trim('/cms/lib/CA01902791/Centricity/Template/GlobalAssets/images///BGimages/DSC_2178.jpg');
            var srcCheck = contentBGSrc.indexOf("Faces/default-man.jpg") > -1;
            var contentBGSrc2 = $.trim('/cms/lib/CA01902791/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg');
            var srcCheck2 = contentBGSrc2.indexOf("Faces/default-man.jpg") > -1;
            var styles = "";

             styles +='.hp-row.one { \n'+
                      	"\t background: rgba("+this.HexToRgb('#EC8C1C')+", 0.1)\n"+
                      '}\n';
            if((contentBGSrc != "") && (!srcCheck) && true) {
                styles += '.hp-row.two { \n'+   
                              "\t background: fixed url('"+contentBGSrc+"') no-repeat; \n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            } else {
                styles += '.hp-row.two { \n'+   
                              "\t background: fixed url('/cms/lib/CA01902791/Centricity/Template/22/hp-region-c-background.jpg') top center no-repeat;\n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            }

            if((contentBGSrc2 != "") && (!srcCheck2) && true) {
                styles += '.hp-row.four { \n'+   
                              "\t background: fixed url('"+contentBGSrc2+"') no-repeat; \n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            } else {
                styles += '.hp-row.four { \n'+   
                              "\t background: fixed url('/cms/lib/CA01902791/Centricity/Template/22/hp-region-f-background.jpg') top center no-repeat;\n"+
                              "\t background-size: cover; \n"+
                          '}\n';
            }

            styles +='.hp-row.two .cs-article-info { \n'+   
                        "\t background: rgba("+this.HexToRgb('#EC8C1C')+", 0.17)\n"+
                    '}\n';

            $('head').append(
                $('', {
                    id: 'cs-dynamic-background-styles',
                    html: styles
                })
            );

        },

