                    var hasFocus = !! ($elem.find(':focus').length > 0);
                    if (! hasFocus) {
                        // Handle blur here.
                        $(".cs-mystart-dropdown.schools .cs-dropdown a").attr("tabindex", "-1");
                		$(this).attr("aria-expanded", "false").parent().removeClass("open").find(".cs-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");   
                    }
                }, 10);
            });            

            $(".cs-mystart-dropdown.schools .cs-selector").hover(function() {
                $(this).attr("aria-expanded", "true").parent().addClass("open").find(".cs-dropdown").attr("aria-hidden","false").slideDown(300, "swing");
            }, function() {

            });
        },

