        "RegionCApps": function() {
            $(".hp-row.two div.ui-widget.app").each(function() {
            	//Split header text
                var splitLength = 3;
            	var headerText = $(".ui-widget-header >*:first-child", this).text();
                headerText = headerText.split(" ");
                var headerTextLength = headerText.length;

                if(headerTextLength > 3) {
                    var headerTexEnd = $.trim(headerText.splice(-splitLength, headerText.length).toString().replace(/,/g, " "));
                } else {
                    var headerTexEnd = $.trim(headerText.splice(-1, headerText.length).toString().replace(/,/g, " "));
                }
                var headerTexBegin = $.trim(headerText.toString().replace(/,/g, " "));
                $(".ui-widget-header >*:first-child", this).html("" + headerTexBegin + "" + headerTexEnd + "");

            	if($(".ui-read-more", this).length) {
                	$(".ui-read-more", this).appendTo($(".ui-widget-header", this));
                }
                if($(".view-calendar-link", this).length) {
                	$(".view-calendar-link", this).appendTo($(".ui-widget-header", this));
                }

                if($(this).hasClass("article-library")) {
            		$(".ui-article", this).each(function(){            
                        $(".clear", this).remove();
                        $(".ui-article-title, .ui-article-description", this).wrapAll('
');
                        $(".cs-article-info", this).appendTo($(".ui-article-thumb", this));

                        if($(".ui-article-description", this).text() == "") {
                        	$(".ui-article-thumb", this).addClass("cs-no-hover");
                        }
                    });
                }
            });
        },

        "RegionFApps": function() {
            $(".hp-row.four .headlines").each(function() {
           		$(".ui-article", this).each(function(){            
                    $(".clear", this).remove();
                    $(".ui-article-title, .ui-article-description", this).wrapAll('
');

                    //Hover
                    $(".cs-article-info", this).hover(function() {
                    	$(this).addClass("hover");
                        $(".ui-article-description", this).slideDown(250);
                    }, function() {
                        $(this).removeClass("hover");
                        $(".ui-article-description", this).slideUp(250);
                    });

                    //Focus
                    $(".ui-article-title a", this).focus(function() {
                    	$(this).parents(".cs-article-info").addClass("hover");
                        $(this).parent().siblings(".ui-article-description").slideDown(250);
                    }).blur(function() {
                        $(this).parents(".cs-article-info").removeClass("hover");
                        $(this).parent().siblings(".ui-article-description").slideUp(250);
                    });
                });
            });

        },

        "UpcomingEvents": function() {
            // FOR SCOPE
            var _this = this;   

            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "no"
                });
                eventsByDay(".upcomingevents .ui-articles");

                $(".ui-widget.app.upcomingevents").each(function() {
                    var viewCalendarLink = $(".view-calendar-link", this).attr("href");

                    $(".ui-articles > li", this).each(function() {
                        if($(".upcoming-column.right .ui-article-description", this).length > 2) {
                            var eventUrl = ((($(".upcoming-column.right .ui-article-description:first-child a", this).attr("href")).split("#")[1]).split("/"));
                            var calendarUrl =  viewCalendarLink + "#" + eventUrl[0] + "/" + eventUrl[1] + "/day";
                            $(".upcoming-column.right", this).append('
More');
                        }
                    });
                });
            }

            function eventsByDay(container) {
                var countLi = 0;
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                });
                $(".sw-calendar-block-time").each(function() {
                	$(this).html($(this).text().replace(/ AM/g, "am"));
                    $(this).html($(this).text().replace(/ PM/g, "pm"));
                });
            }
        },

