$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=8RKb5Id9Ll5y63%2bhbsbBEg%3d%3d&SessionID=d7d3588d-be6f-4068-9e64-bdcdef99fa98&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f26%2f2020+12%3a29%3a20+PM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.16.16';
