        $(document).ready(function () {
            SetCookie('SWScreenWidth', screen.width);
            SetCookie('SWClientWidth', document.body.clientWidth);
            
            $("div.ui-article:last").addClass("last-article");
            $("div.region .app:last").addClass("last-app");

            // get on screen alerts
            var isAnyActiveOSA = 'True';
            var onscreenAlertCookie = GetCookie('Alerts');

            if (onscreenAlertCookie == '' || onscreenAlertCookie == undefined) {
                onscreenAlertCookie = "";
            }
            if (isAnyActiveOSA == 'True') {
                GetContent(homeURL + "/cms/Tools/OnScreenAlerts/UserControls/OnScreenAlertDialogListWrapper.aspx?OnScreenAlertCookie=" + onscreenAlertCookie + "&SiteID=4", "onscreenalert-holder", 2, "OnScreenAlertCheckListItem();");
            }

            

        });

    // ADA SKIP NAV
    $(document).ready(function () {
        $(document).on('focus', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "0px"
            }, 500);
        });

        $(document).on('blur', '#skipLink', function () {
            $("div.sw-skipnav-outerbar").animate({
                marginTop: "-30px"
            }, 500);
        });
    });

    // ADA MYSTART
    $(document).ready(function () {
        var top_level_nav = $('.sw-mystart-nav');

        // Set tabIndex to -1 so that top_level_links can't receive focus until menu is open
        // school dropdown
        $(top_level_nav).find('ul').find('a').attr('tabIndex', -1);

        // my account dropdown
        $(top_level_nav).next('ul').find('a').attr('tabIndex', -1);

        var openNavCallback = function(e, element) {
             // hide open menus
            hideMyStartBarMenu();

            // show school dropdown
            if ($(element).find('ul').length > 0) {
                $(element).find('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).find('.sw-dropdown').find('li:first-child a').focus()
            }

            // show my account dropdown
            if ($(element).next('ul').length > 0) {
                $(element).next('.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false');
                $(element).next('.sw-dropdown').find('li:first-child a').focus();
                $('#sw-mystart-account').addClass("clicked-state");
            }
        }

        $(top_level_nav).click(function (e) {
            openNavCallback(e, this);
        });

        $('.sw-dropdown-list li').click(function(e) {
            e.stopImmediatePropagation();
            $(this).focus();
        });
        
        // Bind arrow keys for navigation
        $(top_level_nav).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).prev('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').last().focus();
                } else {
                    $(this).prev('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }

                // show my account dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).next('.sw-mystart-nav').length == 0) {
                    $(this).parents('div').find('.sw-mystart-nav').first().focus();
                } else {
                    $(this).next('.sw-mystart-nav').focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                // show school dropdown
                if ($(this).find('ul').length > 0) {
                    $(this).find('div.sw-dropdown').css('display', 'block').find('ul').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }

                // show my account dropdown
                if ($(this).next('ul').length > 0) {
                    $(this).next('ul.sw-dropdown').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                
                openNavCallback(e, this);
                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideMyStartBarMenu();
            } else {
                $(this).parent('.sw-mystart-nav').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        // school dropdown
        var startbarlinks = $(top_level_nav).find('ul').find('a');
        bindMyStartBarLinks(startbarlinks);

        // my account dropdown
        var myaccountlinks = $(top_level_nav).next('ul').find('a');
        bindMyStartBarLinks(myaccountlinks);

        function bindMyStartBarLinks(links) {
            $(links).keydown(function (e) {
                e.stopPropagation();

                if (e.keyCode == 38) { //key up
                    e.preventDefault();

                    // This is the first item
                    if ($(this).parent('li').prev('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').prev('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 40) { //key down
                    e.preventDefault();

                    if ($(this).parent('li').next('li').length == 0) {
                        if ($(this).parents('ul').parents('.sw-mystart-nav').length > 0) {
                            $(this).parents('ul').parents('.sw-mystart-nav').focus();
                        } else {
                            $(this).parents('ul').prev('.sw-mystart-nav').focus();
                        }
                    } else {
                        $(this).parent('li').next('li').find('a').first().attr('tabIndex', 0);
                        $(this).parent('li').next('li').find('a').first().focus();
                    }
                } else if (e.keyCode == 27 || e.keyCode == 37) { // escape key or key left
                    e.preventDefault();
                    hideMyStartBarMenu();
                } else if (e.keyCode == 32) { //enter key
                    e.preventDefault();
                    window.location = $(this).attr('href');
                } else {
                    var found = false;

                    $(this).parent('div').nextAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            found = true;
                            return false;
                        }
                    });

                    if (!found) {
                        $(this).parent('div').prevAll('li').find('a').each(function () {
                            if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                                $(this).focus();
                                return false;
                            }
                        });
                    }
                }
            });
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('#sw-mystart-inner').find('.sw-mystart-nav').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() { 
            hideMyStartBarMenu();
        });*/

        // try to capture as many custom MyStart bars as possible
        $('.sw-mystart-button').find('a').focus(function () {
            hideMyStartBarMenu();
        });

        $('#sw-mystart-inner').click(function (e) {
            e.stopPropagation();
        });

        $('ul.sw-dropdown-list').blur(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-mypasskey').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-sitemanager').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-myview').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-signin').focus(function () {
            hideMyStartBarMenu();
        });

        $('#ui-btn-register').focus(function () {
            hideMyStartBarMenu();
        });

        // button click events
        $('div.sw-mystart-button.home a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.pw a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.manage a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('#sw-mystart-account').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).addClass('clicked-state');
                $('#sw-myaccount-list').show();
            }
        });

        $('#sw-mystart-mypasskey a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.signin a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });

        $('div.sw-mystart-button.register a').keydown(function (e) {
            e.stopImmediatePropagation();

            if (e.keyCode == 13) {
                $(this).click();
            }
        });
    });

    function hideMyStartBarMenu() {
        $('.sw-dropdown').attr('aria-hidden', 'true').css('display', 'none');
        $('#sw-mystart-account').removeClass("clicked-state");
    }

    // ADA CHANNEL NAV
    $(document).ready(function() {
        var channelCount;
        var channelIndex = 1;
        var settings = {
            menuHoverClass: 'hover'
        };

        // Add ARIA roles to menubar and menu items
        $('[id="channel-navigation"]').attr('role', 'menubar').find('li a').attr('role', 'menuitem').attr('tabindex', '0');

        var top_level_links = $('[id="channel-navigation"]').find('> li > a');
        channelCount = $(top_level_links).length;


        $(top_level_links).each(function() {
            $(this).attr('aria-posinset', channelIndex).attr('aria-setsize', channelCount);
            $(this).next('ul').attr({ 'aria-hidden': 'true', 'role': 'menu' });

            if ($(this).parent('li.sw-channel-item').children('ul').length > 0) {
                $(this).attr('aria-haspopup', 'true');
            }

            var sectionCount = $(this).next('ul').find('a').length;
            var sectionIndex = 1;
            $(this).next('ul').find('a').each(function() {
                $(this).attr('tabIndex', -1).attr('aria-posinset', sectionIndex).attr('aria-setsize', sectionCount);
                sectionIndex++;
            });
            channelIndex++;

        });

        $(top_level_links).focus(function () {
            //hide open menus
            hideChannelMenu();

            if ($(this).parent('li').find('ul').length > 0) {
                $(this).parent('li').addClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'false').css('display', 'block');
            }
        });

        // Bind arrow keys for navigation
        $(top_level_links).keydown(function (e) {
            if (e.keyCode == 37) { //key left
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').find('> li').last().find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 38) { //key up
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li').addClass(settings.menuHoverClass).find('ul').css('display', 'block').attr('aria-hidden', 'false').find('a').attr('tabIndex', 0).last().focus();
                }
            } else if (e.keyCode == 39) { //key right
                e.preventDefault();

                // This is the last item
                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').find('> li').first().find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) { //key down
                e.preventDefault();

                if ($(this).parent('li').find('ul').length > 0) {
                    $(this).parent('li')
                         .addClass(settings.menuHoverClass)
                         .find('ul.sw-channel-dropdown').css('display', 'block')
                         .attr('aria-hidden', 'false')
                         .find('a').attr('tabIndex', 0)
                         .first().focus();
                }
            } else if (e.keyCode == 13 || e.keyCode == 32) { //enter key
                // If submenu is hidden, open it
                e.preventDefault();

                $(this).parent('li').find('ul[aria-hidden=true]').attr('aria-hidden', 'false').addClass(settings.menuHoverClass).find('a').attr('tabIndex', 0).first().focus();
            } else if (e.keyCode == 27) { //escape key
                e.preventDefault();
                hideChannelMenu();
            } else {
                $(this).parent('li').find('ul[aria-hidden=false] a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        return false;
                    }
                });
            }
        });

        var links = $(top_level_links).parent('li').find('ul').find('a');

        $(links).keydown(function (e) {
            if (e.keyCode == 38) {
                e.preventDefault();

                // This is the first item
                if ($(this).parent('li').prev('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').prev('li').find('a').first().focus();
                }
            } else if (e.keyCode == 40) {
                e.preventDefault();

                if ($(this).parent('li').next('li').length == 0) {
                    $(this).parents('ul').parents('li').find('a').first().focus();
                } else {
                    $(this).parent('li').next('li').find('a').first().focus();
                }
            } else if (e.keyCode == 27 || e.keyCode == 37) {
                e.preventDefault();
                $(this).parents('ul').first().prev('a').focus().parents('ul').first().find('.' + settings.menuHoverClass).removeClass(settings.menuHoverClass);
            } else if (e.keyCode == 32) {
                e.preventDefault();
                window.location = $(this).attr('href');
            } else {
                var found = false;

                $(this).parent('li').nextAll('li').find('a').each(function () {
                    if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                        $(this).focus();
                        found = true;
                        return false;
                    }
                });

                if (!found) {
                    $(this).parent('li').prevAll('li').find('a').each(function () {
                        if (typeof keyCodeMap != "undefined" && $(this).text().substring(0, 1).toLowerCase() == keyCodeMap[e.keyCode]) {
                            $(this).focus();
                            return false;
                        }
                    });
                }
            }
        });

        function hideChannelMenu() {
            $('li.sw-channel-item.' + settings.menuHoverClass).removeClass(settings.menuHoverClass).find('ul').attr('aria-hidden', 'true').css('display', 'none').find('a').attr('tabIndex', -1);
        }
        
        // Hide menu if click or focus occurs outside of navigation
        $('[id="channel-navigation"]').find('a').last().keydown(function (e) {
            if (e.keyCode == 9) {
                // If the user tabs out of the navigation hide all menus
                hideChannelMenu();
            }
        });

        $('[id="channel-navigation"]').find('a').first().keydown(function (e) {
            if (e.keyCode == 9) {
                // hide open MyStart Bar menus
                hideMyStartBarMenu();
            }
        });

        /*$(document).click(function() {
            hideChannelMenu();
        });*/

        $('[id="channel-navigation"]').click(function (e) {
            e.stopPropagation();
        });
    });

    $(document).ready(function() {
        $('input.required').each(function() {
            if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                    $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                }
            }
        });

        $(document).ajaxComplete(function() {
            $('input.required').each(function() {
                if ($('label[for="' + $(this).attr('id') + '"]').length > 0) {
                    if ($('label[for="' + $(this).attr('id') + '"]').html().indexOf('recStar') < 0) {
                        $('label[for="' + $(this).attr('id') + '"]').prepend('* ');
                    }
                }
            });
        });
    });

	// SET UP SOME HANDY VARIABLES
	var logoCheck = "true";
	var iconNum = ("6" == "6;5;4;3;2;1;0") ? "6" : "6";
	var icon1Target = ("true" == "true") ? "_blank" : "_self";
	var icon2Target = ("true" == "true") ? "_blank" : "_self";
	var icon3Target = ("true" == "true") ? "_blank" : "_self";
	var icon4Target = ("true" == "true") ? "_blank" : "_self";
	var icon5Target = ("true" == "true") ? "_blank" : "_self";
	var icon6Target = ("false" == "true") ? "_blank" : "_self";
	var slideshowCheck = "true";
	var slideshowAnimation = ("fade" == "fade;slide") ? "fade" : "fade";
	var isSticky = 0;

	$(window).load(function(){
		if($(window).width() >= 320 && $(window).width() <= 639){
			calendarListView();
		}
	});

	$(function(){
		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ pgScreenSize(); if(logoCheck == "true") { padSiteName(); } headerGlobalH(); slideshowPad(); });
		if($(".hp").length && slideshowCheck == "true") {
			$(window).scroll(adjustChannelDropdowns);
		}

		adjustChannelDropdowns();

		// CHECK FOR SCHOOLLIST
		var useSchoolList = ($(".sw-mystart-dropdown.schoollist").length) ? "yes" : "no";

		// CHECK FOR TRANSLATOR
		var translator = 'false';
		if (translator == "false"){
			$(".sw-mystart-dropdown.translate").hide();
			var useTranslate = "no";
		}
		else {
			addFramesetTranslate();
			var useTranslate = "yes";
		}

		var districtHome = 'true';
		if (districtHome == "false"){
			$(".sw-mystart-button.home").hide();
		}
		else {
			$(".sw-mystart-button.home").show();
		}

		// RESPONSIVE MAIN MENU
		var globalIcons = [
			['Student Portal Infinite Campus', 'https://cocloud1.infinitecampus.org/campus/portal/students/norwood.jsp'],
			['Parent Portal Infinite Campus', 'https://cocloud1.infinitecampus.org/campus/portal/parents/norwood.jsp'],
			['Staff - Infinite Campus', 'https://cocloud1.infinitecampus.org/campus/norwood.jsp'],
			['E-Mail', 'https://mail.google.com/mail'],
			['Athletics', 'https://www.norwoodk12.org/athletics'],
			['Menu', 'http://norwoodk12.org/Page/178']
		];
		var globalLinksHeader = "", globalLinks = "";

		for(var i = 0; i < iconNum; i++) {
			var linkCheck = (globalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : globalIcons[i][1];
			globalLinks += "1, " + globalIcons[i][0] + ", " + linkCheck + ", _self;";
		}

		if(iconNum > 0) {
			globalLinksHeader = "Popular";
		}
		$("body").csRsMenu({
			"breakPoint"		: 5000, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			"slideDirection"	: "left-to-right", // OPTIONS - left-to-right, right-to-left
			"menuButtonParent"	: "#gb-channel-list",
			"showMenuBtnText"	: "yes",
			"menuBtnText"		: "MENU",
			"showSchools"		: useSchoolList,
			"showTranslate"		: useTranslate,
			"translateId"		: "none",
			"showAccount"		: "yes",
			"extraMenuOptions"	: globalLinksHeader, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			"extraMenuItems"	: globalLinks, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			"siteID"		: "4"
		});

		// RESPONSIVE PAGELIST MENU
		if($(".sp").length) {
			$("body").csRsPagelistMenu({
			        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			        "menuButtonParent"      : "#gb-breadcrumbs",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "PAGES",
			        "siteID"                : "4"
			});
		}

		// GLOBAL ICONS
		$("#gb-mystart .gb-global-icons, #gb-mobile-mystart-outer .gb-global-icons").creativeIcons({
			'iconNum'      : iconNum, // NUMBER OF ICONS
			'iconColor'    : '#FFFFFF', // ICON COLOR
			'icon1'        : ['Classroom/5.png', 'Student Portal Infinite Campus', 'https://cocloud1.infinitecampus.org/campus/portal/students/norwood.jsp', icon1Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon2'        : ['Classroom/8.png', 'Parent Portal Infinite Campus', 'https://cocloud1.infinitecampus.org/campus/portal/parents/norwood.jsp', icon2Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon3'        : ['Education Portal/18.png', 'Staff - Infinite Campus', 'https://cocloud1.infinitecampus.org/campus/norwood.jsp', icon3Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon4'        : ['Communication/16.png', 'E-Mail', 'https://mail.google.com/mail', icon4Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon5'        : ['Athletics/2.png', 'Athletics', 'https://www.norwoodk12.org/athletics', icon5Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'icon6'        : ['Classroom/2.png', 'Menu', 'http://norwoodk12.org/Page/178', icon6Target], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
			'siteID'       : '4',
			'siteAlias'    : 'norw2',
			'calendarLink' : '/Page/2',
			'contactEmail' : 'webmaster@norwoodk12.org',
			'onComplete'   : function(){
				$(".gb-global-icons .global-icon").each(function() {
					if(!$(".global-icon-inner", this).length) {
						$(this).children().wrapAll('
');
					}
				});
			}
		});
		if(iconNum == "0") {
			$(".sw-mystart-button.popular, .gb-global-icons").hide();
		} else {
			$(".gb-global-icons").addClass("items-" + $.trim(iconNum));
		}

		pgScreenSize();
		photoGallery();
		useChannelForSection();
		dynamicContent();
		buildStyleSheet();
		gbSearch();	
		headerGlobalH();
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-header .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			stickyChannelBar();
			$(window).scroll(stickyChannelBar);
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			$(window).unbind("scroll", stickyChannelBar);
			stickyChannelBarReset();
			appView();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			appView();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			//DOC MOVE PASSKEYS BUTTON
			if($("#sw-mystart-mypasskey").length) {
				$("#gb-mobile-mystart .gb-header-global.passkey").append($("#sw-mystart-mypasskey"));
			}
			appView();
		}
	}

	function photoGallery(){
		if(slideshowCheck == "true") {
			if(!$("#sw-content-container10.region.ui-hp .rs-photo-gallery-container").length){
				if($("#sw-content-container10.ui-hp div.ui-widget.app.gallery").length) {
					// PHOTO GALLERY ROTATOR
					$("#sw-content-container10.ui-hp div.ui-widget.app.gallery.json").creativeGalleryRotate({
						"imageWidth" 			: 1800,
				        	"imageHeight"			: 960,
				        	"appHeader"			: "no",
				        	"titles"			: "yes",
				        	"captions"			: "yes",
				        	"playPause"			: "no",
				        	"mainControls"			: "yes",
				        	"linkedElement"             	: [""], // ["image", "title", "overlay", "all"]
				        	"contMoreLink"			: "no",
				       		"contMoreLinkText"		: "Read More",
				        	"descMoreLink"			: "yes",
				        	"descMoreLinkText"		: "MORE",
				        	"bullets"			: "yes",
				        	"bulletControls"		: "no",
				        	"thumbnails"			: "no",
				        	"thumbnailWidth"		: 122,
				        	"thumbnailHeight"		: 53,
				        	"thumbnailSlideDirection"	: "horizontal", // horizontal or vertical
				        	"hoverPause"			: "yes",
				        	"fullScreenRotator"		: "yes",
				        	"fullScreenBreakpoints"		: ["960"], // OPTIONS ARE ["960", "768", "640", "480", "320"]
				        	"autoRotate"			: "yes",
				        	"transitionType"		: slideshowAnimation, // fade or slide
				        	"transitionSpeed"		: 2,
				        	"onImageLoad"			: function() { },
				        	"allImagesLoaded"		: function() { },
				        	"onThumbnailLoad"		: function() { },
				        	"allThumbnailsLoaded"		: function() { },
				        	"onTransitionStart"		: function() { },
				        	"onTransitionEnd"		: function() {
				        		// SET PREV AND NEXT CONTROL IMAGES
				        		var prevImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev img").attr("src");
				        		var prevImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev img").attr("title");
				      			var nextImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.next img").attr("src");
				      			var nextImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.next img").attr("title");
				        		$("#hp-slideshow .rs-photo-gallery-control.back .rs-photo-gallery-control-img img").attr("src", prevImgSrc).attr("title", prevImgTitle).attr("alt", prevImgTitle);
				        		$("#hp-slideshow .rs-photo-gallery-control.next .rs-photo-gallery-control-img img").attr("src", nextImgSrc).attr("title", nextImgTitle).attr("alt", nextImgTitle);
				        	},
				        	"allLoaded"			: function() {
				        		// WRAP THE SLIDESHOW DESCRIPTION CONTAINER
				        		$("#hp-slideshow .rs-photo-gallery-desc").wrap('
').children().wrapAll('
');
				        		$("#hp-slideshow .rs-photo-gallery-desc-inner").children().wrapAll('
');

				        		// MOVE THE SLIDESHOW BULLETS
				        		$("#hp-slideshow .rs-photo-gallery-images").prepend($("#hp-slideshow .rs-photo-gallery-bullets-outer"));

				        		// MOVE SLIDESHOW CONTROLS
				        		$("#hp-slideshow .rs-photo-gallery-images").prepend($("#hp-slideshow .rs-photo-gallery-control.next")).prepend($("#hp-slideshow .rs-photo-gallery-control.back"));

				        		// SET PREV AND NEXT CONTROL IMAGES
				        		var prevImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev .record-src").text();
				        		var prevImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.prev .record-title").text();
				      			var nextImgSrc = $("#hp-slideshow .rs-photo-gallery-images-list .image.next .record-src").text();
				      			var nextImgTitle = $("#hp-slideshow .rs-photo-gallery-images-list .image.next .record-title").text();
				        		$("#hp-slideshow .rs-photo-gallery-control.back").append('
');
				        		$("#hp-slideshow .rs-photo-gallery-control.next").append('
');

				        		// SET BOTTOM PADDING ON THE SLIDESHOW SO THAT EVERYTHING IS VISIBILE ABOVE THE CHANNEL BAR
				        		slideshowPad();

				        		// SET CONTROL HOVER
				        		$(".rs-photo-gallery-control").hover(function() {
				        			$(".rs-photo-gallery-control-img", this).fadeIn("fast");
				        		}, function () {
				        			$(".rs-photo-gallery-control-img", this).stop(true, true).fadeOut("fast");
				        		});
				        	}
					});
				} else {
					if($(".sw-special-mode-bar").length) {
						$("#hp-slideshow #sw-content-container10").html('




');
						$("").load(function() {
							$("#hp-slideshow .rs-photo-gallery-images-list li").append($(this));
							photoGalleryPlaceholder();
							$(window).bind("resize", function() { photoGalleryPlaceholder(); });
						});
					}
				}
			}
		}
	}

	function slideshowPad() {
		var channelBarH = $("#gb-channel-list-outer").outerHeight(true);

		$("#hp-slideshow .rs-photo-gallery-container, #hp-slideshow .rs-photo-gallery-images, #hp-slideshow .rs-photo-gallery-images-list, #hp-slideshow .rs-photo-gallery-images-list li, #hp-slideshow .rs-photo-gallery-desc-outer, #hp-slideshow .rs-photo-gallery-bullets-outer").css("padding-bottom", channelBarH + "px");
	}

	function headerGlobalH() {
		$("#gb-header-globals-outer").height("auto");

		var headerH = $("#gb-header").outerHeight(true);
		$("#gb-header-globals-outer").css("height", headerH + "px");
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content-outer div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		//DOC accordion header click
		$("#hp-content-outer div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("#hp-content-outer div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});

			// MANUALLY TRIGGER THE WINDOW RESIZE EVENT
			// TO MAKE SURE APP SLIDERS PROPERLY RESIZE
			$(window).trigger("resize");
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
        }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();

		//DOC replace mystart dropdown arrows
		$(".sw-dropdown-arrow").addClass("euphoria-icon").text("d");

		//DOC #swsignin-txt-username -- AUTO-FOCUS USERNAME SIGNIN FORM FIELD #swsignin-txt-username
		$("#swsignin-txt-username").focus();

		//DOC move mystart buttons
		if($(".sw-mystart-button.home").length) {
			$(".sw-mystart-button.home").insertAfter($("#gb-mystart .sw-mystart-button.sitemap"));
		}
		if($(".sw-mystart-dropdown.schoollist").length) {
			$(".sw-mystart-dropdown.schoollist .selector").text("Schools");
			$(".sw-mystart-dropdown.schoollist").insertAfter($("#gb-mystart .sw-mystart-button.sitemap"));
		}
		if("false" == "true") {
			$(".sw-mystart-dropdown.translate").insertBefore($("#gb-mystart .sw-mystart-button.sitemap"));
		}

			$(".gb-header-global.manage").append($(".sw-mystart-button.signin"));
			if($(".sw-mystart-button.register").length){
				$(".gb-header-global.manage").append($(".sw-mystart-button.register"));
			}


		// ********** HEADER ********** //
		var taglineToggle = 'true';
		var sitenameToggle = 'true';
		if(taglineToggle == "false" && sitenameToggle == "false") {
			$("#gb-sitename-outer").hide();
		}
		//DOC add logo
		var logoSrc = jQuery.trim("/cms/lib/CO01900783/Centricity/Template/GlobalAssets/images///Logos/NorwoodMavericksLogo-150x130.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$('').one("load", function() {
					$(".gb-logo").append($(this)).children().wrap('');

					// APPLY PADDING TO THE SITE NAME AND TAGLINE
					padSiteName();

					// MANUALLY TRIGGER THE WINDOW RESIZE EVENT
					// TO MAKE SURE SLIDESHOW PROPERLY SIZES TO VIEWPORT
					$(window).trigger("resize");
				});
			}
		}

		// HEADER GLOBAL TARGETS
		var global1Target = "false";
		var global2Target = "false";
		var global3Target = "true";
		if(global1Target == "true") {
			$(".gb-header-global.global-one a").attr("target", "_blank");
		}
		if(global2Target == "true") {
			$(".gb-header-global.global-two a").attr("target", "_blank");
		}
		if(global3Target == "true") {
			$(".gb-header-global.global-three a").attr("target", "_blank");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		//DOC set icon for home channel
		var firstChannelText = $("li.sw-channel-item:first > a span").text().toLowerCase();
		if(firstChannelText == "home") {
			$("li.sw-channel-item:first > a span").addClass("euphoria-icon").text("h");
			$("li.sw-channel-item").first().addClass("home");
		}
		//DOC set icon for calendar channel
		var lastChannelText = $("li.sw-channel-item:last > a span").text().toLowerCase();
		if(lastChannelText == "calendar") {
			$("li.sw-channel-item:last > a span").addClass("euphoria-icon").text("c");
			if($("li.sw-channel-item.home").length) {
				$("li.sw-channel-item").last().addClass("calendar").insertAfter($("li.sw-channel-item").first());
			} else {
				$("li.sw-channel-item").last().addClass("calendar").insertBefore($("li.sw-channel-item").first());
			}
		}
		// SET CLASS TO MAKE CHANNEL DROPDOWNS DROP UP IF SCROLL POSITION IS LESS THAN 40 PIXELS FROM TOP EDGE OF WINDOW
		if($(".hp").length && slideshowCheck == "true") {
			if ($(window).scrollTop() <= 60) {
				$("#gb-channel-list").addClass("channel-drop-up");
			} else {
				$("#gb-channel-list").removeClass("channel-drop-up");
			}
		}
		// CHECK IF CHANNEL HAS DROPDOWN
		$(".sw-channel-item").each(function() {
			if($(".sw-channel-dropdown", this).length) {
				$(this).addClass("has-dropdown");
			}
		});

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC replace more-link text
		$("a.more-link span, a.view-calendar-link span").text("VIEW ALL");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        	if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
        		$("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
        		//DOC add signed out breadcrumb
        		$(".ui-breadcrumbs").append("
Signed Out");
        	}
        	//DOC add sign in breadcrumb
        	if($("#swlogin").length){
        		$(".ui-breadcrumbs").append("
Sign In");
        	}
        	//DOC add register breadcrumb
        	if($(".spn").length && $("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
        		$(".ui-breadcrumbs").append("
Register");
        	}

        	//DOC add read more button to each healine
        	$(".ui-widget.app.headlines").each(function(){
        		$(".ui-article", this).each(function(){
        			var headlineURL = $("h1 a", this).attr("href");
        			$(this).append('READ MORE');
        		});
        	});

        	// APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        	$("div.ui-widget.app .ui-widget-detail img")
        		.not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
        		.not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
        		.not($("div.ui-widget.app.headlines .ui-widget-detail img"))
        		.each(function() {
        			if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
					$(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
				}
		});

		// MOBILE MYSTART GLOBAL ICONS REVEAL
		$("#gb-mobile-mystart-outer .sw-mystart-button.popular a").click(function(e) {
			e.preventDefault();
			if($("#gb-mobile-mystart-outer .gb-global-icons-outer").hasClass("active")) {
				$("#gb-mobile-mystart-outer .gb-global-icons-outer").slideUp(function() {
					$(this).removeClass("active");
				});
			} else {
				$("#gb-mobile-mystart-outer .gb-global-icons-outer").slideDown(function() {
					$(this).addClass("active");
				});
			}
		});

		// REMOVE BOTTOM CONTENT REGION IF NO APPS EXIST
		if(!$(".hp-content.bottom .ui-widget.app").length) {
			$(".hp-content.bottom").hide();
		}

		// MYSTART GLOBAL ICONS REVEAL
		$("#gb-mystart .sw-mystart-button.popular").hover(function() {
			$("#gb-mystart .gb-global-icons-outer").slideDown("fast");
		}, function() {
			$("#gb-mystart .gb-global-icons-outer").stop(true, true).slideUp("fast");
		});
		/*
		$("#gb-mystart .sw-mystart-button.popular a").click(function(e) {
			e.preventDefault();
			if($("#gb-mystart .gb-global-icons-outer").hasClass("active")) {
				$("#gb-mystart .gb-global-icons-outer").slideUp(function() {
					$(this).removeClass("active");
				});
			} else {
				$("#gb-mystart .gb-global-icons-outer").slideDown(function() {
					$(this).addClass("active");
				});
			}
		});
		*/

		// MOVE VIEW ALL LINKS FOR HEADLINES AND UPCOMING EVENTS
		$("#sw-content-container1.ui-hp .ui-widget.app").each(function() {
			if($(this).hasClass("headlines")) {
				if($(".more-link", this).length) {
					if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || !$(".ui-widget-header h1", this).length || $(".ui-widget-header h1", this).text() == "") {
						$(".ui-widget-detail", this).prepend($(".more-link", this).addClass("cs-header-view-all no-header").text("VIEW ALL"));
					} else {
						$(".ui-widget-header h1", this).append($(".more-link", this).addClass("cs-header-view-all").text("VIEW ALL"));
					}
				}
			} else if($(this).hasClass("upcomingevents")) {
				if($(".view-calendar-link", this).length) {
					if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || !$(".ui-widget-header h1", this).length || $(".ui-widget-header h1", this).text() == "") {
						$(".ui-widget-detail", this).prepend($(".view-calendar-link", this).addClass("cs-header-view-all no-header").text("VIEW ALL"));
					} else {
						$(".ui-widget-header h1", this).append($(".view-calendar-link", this).addClass("cs-header-view-all").text("VIEW ALL"));
					}
				}
			}
		});

		// ADD LAST CLASS TO LAST EVENT AND LAST HEADLINE
		$(".upcomingevents li").last().addClass("last");
		$(".headlines li").last().addClass("last");

		// CHANGE ORDER OF UPCOMING EVENTS TITLE AND TIME
		$(".upcomingevents .ui-article-description").each(function() {
			$(this).prepend($(".sw-calendar-block-title", this));
		});

		// EVENTS SCROLLER
        	if($(".hp").length) {
        		$("#sw-content-container1.region.ui-hp div.ui-widget.app.upcomingevents").each(function() {
		    		$(".ui-widget-detail", this).wrap('

');
		    	});

	    		$("#sw-content-container1.region.ui-hp div.ui-widget.app").appScroller({
		           	"appParent"             : ".hp-content.top",
		        	"targetApp"             : "upcomingevents", //upcomingevents or headlines
		        	"imageNum960"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 960
		        	"imageNum768"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 768
		       		"imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
		       		"imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
		        	"imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
		        	"backIconFontLetter"    : "<", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		        	"nextIconFontLetter"    : ">", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		        	"buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
		        	"extraBackIconClass"    : "euphoria-icon border-box",
		        	"extraNextIconClass"    : "euphoria-icon border-box",
		        	"controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
		    	});

		    	$("#sw-content-container1.region.ui-hp div.ui-widget.app.upcomingevents").each(function() {	
		    		$(".ui-widget-detail-outer", this).prepend($("div.back", this)).append($("div.next", this));
		    		var moreLink = $(".view-calendar-link.cs-header-view-all", this).clone();
		    		var backBtn = $("div.back", this).clone();
		    		var nextBtn = $("div.next", this).clone();
		    		$(".ui-app-slider-content", this).prepend('
');
		    		$(".ui-slider-controls", this).prepend(moreLink).prepend(backBtn).append(nextBtn);
			});
		}

		// HEADLINES SCROLLER
		if($(".hp").length) {
			$("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines").each(function() {
		    		$(".ui-widget-detail", this).wrap('

');
		    	});

	    		$("#sw-content-container1.region.ui-hp div.ui-widget.app").appScroller({
		           	"appParent"             : ".hp-content.top",
		        	"targetApp"             : "headlines", //upcomingevents or headlines
		        	"imageNum960"           : 3, //NUMBER OF IMAGES TO DISPLAY AT 960
		        	"imageNum768"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 768
		       		"imageNum640"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 640
		       		"imageNum480"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 480
		        	"imageNum320"           : 1, //NUMBER OF IMAGES TO DISPLAY AT 320
		        	"backIconFontLetter"    : "<", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		        	"nextIconFontLetter"    : ">", //LETTER OR NUMBER TO USE FOR THE ICON FONT FOR THE BACK BUTTON
		        	"buttonColor"           : "#FFF", //COLOR TO BE USED FOR THE BACK AND NEXT BUTTONS
		        	"extraBackIconClass"    : "euphoria-icon border-box",
		        	"extraNextIconClass"    : "euphoria-icon border-box",
		        	"controlGutter"         : "0" //LEFT and RIGHT MARGINS TO ALLOW ROOM FOR CONTROLS
		    	});

		    	$("#sw-content-container1.region.ui-hp div.ui-widget.app.headlines").each(function() {
		    		$(".ui-widget-detail-outer", this).prepend($("div.back", this)).append($("div.next", this));
		    		var moreLink = $(".more-link.cs-header-view-all", this).clone();
		    		var backBtn = $("div.back", this).clone();
		    		var nextBtn = $("div.next", this).clone();
		    		$(".ui-app-slider-content", this).prepend('
');
