function resize_sp1632() {
	var windowWidth = Math.max(parseFloat("1200"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("1100");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel1632Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel1632Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel1632Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel1632Div").style.height = Math.max((parseFloat("500")/parseFloat("1200") * windowWidth), parseFloat("500")) + "px";
	document.getElementById("carousel1632Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel1632Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resize_sp1632(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resize_sp1632(); });
}
resize_sp1632();

div.script:
// Equalize height 
function equalHeight(parent, children) {
	// Define height 
	var bxSlider = document.querySelectorAll(parent);
	var maxHeight = document.querySelector(children).clientHeight;
	
	// Loop through each LI and add min height
	for (i = 0; i < bxSlider.length; i++) { 
		bxSlider[i].style.minHeight = maxHeight - 2 + "px";
	}// End loop 
}

function adaptiveThumbHeight(parentID) {
	var totalNumThumbs = document.querySelectorAll(parentID + ' .news-thumb');
	for (var i = 0; i < totalNumThumbs.length; i++) { 
		  var thumbContainerHeight = totalNumThumbs[i].offsetHeight; 
		  var thumbImgHeight = totalNumThumbs[i].querySelector('img').height;
		  var totalDiff = (thumbContainerHeight - thumbImgHeight) / 2;
		  
		  if (thumbImgHeight > thumbContainerHeight) {
			  totalNumThumbs[i].className += " tallImage";
		  }
		  
		  if (totalDiff > 0) {
		  	totalNumThumbs[i].querySelector('img').style.marginTop = totalDiff + "px";
		  }
	}
}

