function resize_sp952() {
	var windowWidth = Math.max(parseFloat("1500"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("980");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel952Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel952Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel952Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel952Div").style.height = Math.max((parseFloat("409")/parseFloat("1500") * windowWidth), parseFloat("409")) + "px";
	document.getElementById("carousel952Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel952Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resize_sp952(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resize_sp952(); });
}
resize_sp952();

