	function pgScreenSize() {
		//var getWidth = ($("body").hasClass("msie8")) ? screen.width : window.outerWidth;
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			featureRotateTimer();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
			appView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
			appView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			photoGallery();
			appView480();
			appView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			photoGallery();
			appView480();
			appView();
			if(featureTimerSet == 1){
				clearInterval(window.featureTimer);
				featureTimerSet = 0;
			}
		}
	}

	function photoGallery(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length){
			// PHOTO GALLERY CONFIGURATION
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMulimediaRotator({
				"imageWidth"		: 960,
				"imageHeight"		: 530,
				"titles"		: "yes",
				"captions"		: "yes",
				"playPause"		: "no",
				"mainControls"		: "no",
				"bullets"		: "yes",
				"bulletControls"	: "no",
				"hoverPause"		: "yes",
				"transitionSpeed"	: 3,
				"transitionDelay"	: 8,
				"onImageLoad"		: function(){ },
				"onTransitionStart"	: function(){ },
				"onTransitionEnd"	: function(){ },
				"watchVidClick"		: function(){ },
				"closeVidClick"		: function(){ },
				"allLoaded"		: function(){ $(".rs-gallery-title").prepend("
"); $(".rs-gallery-caption").prepend("
"); }
			});
		}
	}

	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}
		});

		if(!$("div.ui-widget.app.active").length){
			$(".hp.column.one div.ui-widget.app:first").addClass("active");
		}
		$("#hp-content div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}	

	function appView480() {
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("#hp-feature-apps div.ui-widget.app.active").length){
			$("#hp-feature-apps div.ui-widget.app:first").addClass("active");
		}
		$("#hp-feature-apps div.ui-widget-header").click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");

			$("html, body").animate({
				scrollTop: $("#" + clickedApp).offset().top
			}, 500, function(){
				$("html, body").stop(true, false);
			});
		});
	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
