			$("#gb-emergency-announcement .ui-widget.app.announcements .ui-article-description:contains('[ALERT]')").addClass("sw-alert");
			if($(".sw-alert").size()){
				$(".sw-alert").each(function(){
					stripAlert = $(this).html().replace(/\[ALERT\]/i, "");
			       	 	$(this).html(stripAlert);
		       	 	});
			}
			$("#gb-emergency-announcement").show();
		}

