				$("#gb-popular-links-outer").slideDown(function(){
					$(".sw-mystart-button.popular-links").addClass("open");
					$(this).addClass("open");
				});
			} else {
				$("#gb-popular-links-outer").slideUp(function(){
					$(".sw-mystart-button.popular-links").removeClass("open");
					$(this).removeClass("open");
				});
			}
		});	

