    function LoadRating891136321() {
    var ModuleInstanceID = 891;
    var FlexDataID = 136321;

        GetContent("https://www.leonschools.net//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating891136321", 2);
}

    function InsertRating891136321() {
    var ModuleInstanceID = 891;
    var FlexDataID = 136321;
    var Rating = $('#swmoduleinstancerating-hid-rating891136321').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating891136321();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.leonschools.net//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating891136321']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating891136321Values();
                InsertRating891136321($('#swmoduleinstancerating-hid-rating891136321').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating891136321Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating891136321']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating891136321').val((i + 1) / 2);
            }
        });
    }

