    function LoadRating50694132357() {
    var ModuleInstanceID = 50694;
    var FlexDataID = 132357;

        GetContent("https://www.leonschools.net//site/UserControls/Rating/RatingListWrapper.aspx?ModuleInstanceID=" + ModuleInstanceID + "&FlexDataID=" + FlexDataID + "&IsDisabled=true", "swmoduleinstancerating-pnl-rating50694132357", 2);
}

    function InsertRating50694132357() {
    var ModuleInstanceID = 50694;
    var FlexDataID = 132357;
    var Rating = $('#swmoduleinstancerating-hid-rating50694132357').val();
    var data = "{ModuleInstanceID: " + ModuleInstanceID + ", " +
                "FlexDataID: " + FlexDataID + ", " +
                "Rating: " + Rating + "} ";  
    var success = 'LoadRating50694132357();';
    var failure = "CallControllerFailure(result[0].errormessage);";

    if (Rating != '' && Rating !== undefined) {
        CallController("https://www.leonschools.net//site/UserControls/Rating/RatingController.aspx/InsertRating", data, success, failure);
    }
}

    $(document).ready(function() {
        CheckScript('Rating');
        CheckScript('Metadata');

        // initialize rating script
        $("input[name='swmoduleinstanceratinghidrating50694132357']").rating({
            callback: function() {
                Getswmoduleinstanceratinghidrating50694132357Values();
                InsertRating50694132357($('#swmoduleinstancerating-hid-rating50694132357').val());
            }
        });
    });
    
    // fill hidden input with value clicked
    function Getswmoduleinstanceratinghidrating50694132357Values() {
        var selItem;

        $("input[name='swmoduleinstanceratinghidrating50694132357']").each(function(i) {
            if ($(this).is(':checked')) {
                $('#swmoduleinstancerating-hid-rating50694132357').val((i + 1) / 2);
            }
        });
    }

