div.script:
$(function(){
	$('#district-alert .alert-wrapper').each(function(){
		var $wrapper=$(this);
		var id=$wrapper.attr("data-id");
		if (!$.cookie('ALERT_' + id)) {
			$wrapper.show();
			$wrapper.find(".alert-close").on("click", function(){
				$.cookie('ALERT_' + id, "hide", { expires: 7 })
				$wrapper.slideUp();
			});
		}
	});
});

$(function() {
    $('.carousel').each(function(){
        $(this).carousel({
            interval: 5000
        });
    });
});

$(document).ready(function() {
	$("#myCarousel").swiperight(function() {
		$("#myCarousel").carousel('prev');
	});
	$("#myCarousel").swipeleft(function() {
		$("#myCarousel").carousel('next');
	});
});

$("#news-module .tab-pane ul li a").ellipsis({ live:true });

$("#info-module .tab-pane").each(function(el){
	var $container=$(this)
	var $resources=$container.find(".resources");
	var $showmoreless=$('

 showhide resources ');
	$resources.append($showmoreless);
	$showmoreless.on("click", function(event){
		var $this=$(this);
		var $bottom=$container.find(".bottom");
		var $more=$container.find(".more");
		var state=$this.hasClass("active");
		event.preventDefault();
		
		if(state){
			$this.removeClass("active");
			$more.slideUp().removeClass("active");
			$bottom.addClass("active");
		} else {
			$this.addClass("active");
			$more.slideDown().addClass("active");
			$bottom.removeClass("active");
/*
			$more.masonry({
				itemSelector:"ul"
				, gutter:".gutter-sizer"
			});
*/
		}
	});
	$container.find(".school-listbytype .groups ul li.header").on("click", function(event){
		var $this=$(this);
		var $ul=$this.parent("ul");
		event.preventDefault();
		
		$ul.toggleClass("active");
	});
});
