div.script:
$(function() {
	$("#navbar .nav-panel-level-1").menuAim({
		rowSelector: "> .nav-item"
		, submenuSelector: " .dropdown"
		, submenuDirection: "below"
		, activate: function(el) {
			var $el = $(el);
			var $sub = $el.find(">.collapse");
			var $toggle = $el.find(">.nav-toggle");
			var $panel = $sub.find(">.nav-panel");
			$sub.addClass("over")
			if($sub.length > 0) {
				$toggle.addClass("on");
			}
			jQuery('a').filter(function(){
				// this is an ugly hack to get specific items to open in new windows
				// give links that need new windows background-clip: content-box in the skin CSS
				return window.getComputedStyle(this).backgroundClip == 'content-box';
			}).attr('target','_new');
		}
		, deactivate: function(el) {
			$(el).find(">.collapse").removeClass("over");
			$(el).find(">.nav-toggle").removeClass("on");
		}
		, exitMenu: function() {
			return true;
		}
	});
});

$(function() {
	var $menu = $('#district-menu');
	var $list = $('#district-menu .school-container');
	var $schools = $list.find('.school');
	var $tabs = $('#district-menu .school-type-tabs li');
	var $search = $('#district-menu .school-search');
	var $searchbutton = $search.find('.search-button');
	var $searchinput = $search.find('.search-input input');

	$tabs.each(function(i, o) {
		var $this=$(this);
		
		if($schools.filter($this.attr('data-filter')).length == 0) {
			$this.hide();
		}
	})
	
	$menu.click(function(event){
		event.stopPropagation();
	});
	
	$tabs.click(function(event) {
		event.preventDefault();
		var $this=$(this);
		if($this.hasClass('active')) {
			$this.removeClass('active');
			$schools.addClass('active');
		} else {
			$tabs.removeClass('active');
			$this.addClass('active');
			$schools.removeClass('active');
			$schools.filter($this.attr('data-filter')).addClass('active');
			//console.log($this.attr('data-filter'), $tabs.filter($this.attr('data-filter')));
		}
	});
	
	var cancelSearch = function() {
		$searchinput.val('');
		$schools.addClass('active');
		$search.removeClass('open');
	}
	
	$searchinput.keyup(function(event){
		if(event.keyCode == 27) { // escape
			event.stopPropagation();
			cancelSearch();
			return;
		}
		var valThis = $(this).val().toLowerCase();
		if(valThis == ""){
			$schools.removeClass('active');
		} else {
			$schools.each(function(){
				var $this = $(this);
				var text = $this.find('.title').text().toLowerCase();
				$this.toggleClass('active', (text.indexOf(valThis) >= 0));
			});
		};
	});
	
	$searchbutton.click(function(event) {
		event.preventDefault();
		
		if($search.hasClass('open')) {
			cancelSearch();
		} else {
			$search.addClass('open');
			$searchinput.focus();
			$schools.removeClass('active');
		}
	});
});

jQuery(function(){
	Galleria.loadTheme("/_resources/jscripts/galleria/themes/gsl/galleria.gsl.js?cb=305F2277-5056-9010-350DD505F5183E55");
	Galleria.run("#gallery_images_5b11058a-5ea0-4c98-9192-c0253e94ad43", {
		height:
		
			0.75
		
		, keepSource: true
		, imageCrop: false 
		, dataConfig: function(img) {
			return {
				title: $(img).attr("data-title")
				, description: $(img).attr("data-description")
				, big: $(img).attr("data-big")
			}
		}
		, extend: function(options) {
			var gallery = this;   
			this.bind('image', function(e) {
				var curr = gallery.getData(gallery.getIndex());
				var currOrig = curr.original;
				var altTxt = $(currOrig).attr('data-title');
				$(e.imageTarget).attr('alt',altTxt);
			});
			this.bind('thumbnail', function(e) {
				var thumb = gallery.getData(e.index);
				var thumbOrig = thumb.original;
				var altText = $(thumbOrig).attr('data-title');
				$(e.thumbTarget).attr('alt',altText);
			});
		}
		//, thumbnails: "lazy"ish
		, autoplay: 7000
	});
});

function googleTranslateElementInit() {
new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.InlineLayout.SIMPLE, autoDisplay: false}, 'google_translate_element');
}

