var curActive = 1;

$(document).ready(function(){
	switchTabs('1');
}); 

//accepts a number, and changes the current tab to be that number
function switchTabs(whichTab){
	if(curActive != whichTab){
		$('#sw-tabBar').css("backgroundImage", 'url(pix2013/tabs/tab'+whichTab+'Active.png)');
		$('#sw-tab'+curActive+'Content').slideUp( function(){ $('#sw-tab'+whichTab+'Content').slideDown( function(){ $('#sw-tab'+whichTab+'Content').css("minHeight", "1%"); try{resize();}catch(err){} }); });
		curActive = whichTab;
	}
}

