input.script:
        $(function () {
            App.init();

            if ($('#alert-message').val()) {
                var msg = $('#alert-message').val().split(":");
                displayMessage(msg[1], msg[0]);
            }

            $(".bootstrap-switch").bootstrapSwitch();

            $(".search-form").on("submit", function (event) {
                event.preventDefault();
                window.location = '/Directory/Search\#stq=' + $(event.target).find("input").val() + '&stp=1';
            });
        });

        var toggleWorking = function (el, text) {
            //Button has been toggled as working already
            if ($(el).is(":disabled")) {

                var oldText = $(el).data('old-text');
                var oldIcon = $(el).data('old-icon');

                if (oldText !== undefined) {
                    $(el).text(text);
                }

                if (oldIcon !== undefined) {
                    $(el).find("i").attr('class', oldIcon);
                }
                else {
                    $(el).find("i").remove();
                }

                $(el).removeAttr('disabled');

            }
            else {
                $(el).data('old-text', $(el).text());

                if (text !== undefined) {
                    $(el).text(text);
                }

                if ($(el).find("i").length > 0) {
                    $(el).data('old-icon', $(el).find("i").attr('class'));
                    $(el).find("i").attr('class', 'fa fa-spinner fa-spin');
                }
                else {
                    $(el).prepend($("").attr('class', 'fa fa-spinner fa-spin').css('margin-right', '5px'));
                }

                $(el).attr('disabled', 'disabled');
            }
        };
        $("body").on("click", ".toggle-working", function (el) {
            toggleWorking($(el.currentTarget));
            return true;
        });


        var displayMessage = function (message, msgType) {
            toastr.options = {
                closeButton: false,
                debug: false,
                newestOnTop: true,
                progressBar: true,
                positionClass: "toast-bottom-right",
                onClick: null,
                showDuration: '300',
                hideDuration: '1000',
                timeOut: '10000',
                extendedTimeOut: '3000',
                showEasing: 'swing',
                hideEasing: 'linear',
                showMethod: 'fadeIn',
                hideMethod: 'fadeOut'
            };

            toastr[msgType](message);
        };

        $(function () {

            //Master Slider - Full Width
            var slider = new MasterSlider();
            slider.setup('masterslider', {
                width: 800,
                height: 250,
                fullwidth: true,
                speed: 20,
                view: 'flow',
                loop: true,
                space: 20,
                autoplay: true,
                preload: "all"
            });

            //slider.control('arrows');
            slider.control('timebar', { color: "#ffd784" });

        });

        $(".likes").on('click', function (event) {
            event.preventDefault();
            if (!$(event.currentTarget).hasClass('liked')) {
                $.ajax({
                    url: '/News/Like' + "/" + $(event.currentTarget).attr('data-id'),
                    success: function (data) {
                        console.log(data, event);
                        $(event.currentTarget).find('span').text('(' + data + ')');
                        $(event.currentTarget).addClass('liked');
                    },
                    error: function (xhr, status, error) {
                        console.log(xhr.responseText + "\n" + status + "\n" + error);
                    }
                });
            }
        })

