div.script:
function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
                        includedLanguages: 'ar,zh-CN,en,es',
                    layout: google.translate.TranslateElement.InlineLayout.VERTICAL,
                        gaTrack: true,
            gaId: '123'
                }, 'google_translate_element');

                // remove the powered by
        // select all direct children but the first div and remove them
        // need to include text nodes
        $('#google_translate_element > div.skiptranslate').contents().slice(1).remove();
        }

        Campussuite.Widgets.run({
    "styles": [
        "\/ui\/overrides\/owl-carousel\/owl.carousel.min.css"
    ],
    "scripts": [
        "\/ui\/overrides\/owl-carousel\/owl.carousel.min.js"
    ],
    "plugin": "OwlSlider",
    "pluginId": "cms-widget-d97e3a4a-9fd8-4c97-be56-d5b69c107644",
    "pluginOptions": {
        "transition": "fade",
        "transitionSpeed": 10,
        "navigation": true,
        "pagination": false,
        "paginationNumbers": false,
        "paginationSpeed": 5000,
        "autoplay": true,
        "pauseOnHover": true,
        "autoHeight": true
    }
});

(function (window, document, $, undefined) {
    $(function () {
        $('#cms-widget-63a693ff-64ed-4ed8-8fc4-dc46568ed504').on('click', '.meta-event-attachment-link', function (e) {
            var $target = $(e.currentTarget);
            var p = $.ajax({
                url: $target.attr('data-ajax'),
            });
            p.done(function (resp) {
                $(resp)
                    .appendTo($('body'))
                    .modal({
                        show: false
                    })
                    .on('show.bs.modal', function () {
                        $(this).css('display', 'block');
                        var $d = $(this).find('.modal-dialog'),
                            o = ($(window).height() - $d.height()) / 2,
                            bm = parseInt($d.css('marginBottom'), 10);
                        o = Math.max(o, bm);
                        $d.css('margin-top', o);
                    })
                    .on('hidden.bs.modal', function () {
                        $(this).remove();
                    })
                    .modal('show');
            });
            e.preventDefault();
        });
    });
})(window, document, jQuery);

