link.script:
    $('#navigation').slicknav({
        label: 'Menu',
        prependTo: '#mobile_top_nav',
        allowParentLinks: true,
    });

    var z = 0;
    $(document).ready(function () {
        districtNav = $("nav");
        if ($("#closings1_divAlertBanner").height() != null){
            z += $("#closings1_divAlertBanner").height()
        }
        if ($("#closings1_divMinimizedBanner").outerHeight() != null){
            z += $("#closings1_divMinimizedBanner").outerHeight();
        }
        var y = $(document).scrollTop();
        if (y >= z) {
            districtNav.css({ position: "fixed", "top": "0px" }),
		    districtNav.css({ width: "100%" });
        }
        else {
            districtNav.css("position", "absolute"),
            districtNav.css("top", z + "px"),
		    districtNav.css("width", "100%");
        }

        $(document).scroll(function () {
            var y = $(document).scrollTop(), //get page y value 
            z = 0;
            if ($("#closings1_divAlertBanner").height() != null){
                z += $("#closings1_divAlertBanner").height()
            }
            if ($("#closings1_divMinimizedBanner").outerHeight() != null){
                z += $("#closings1_divMinimizedBanner").outerHeight();
            }
            if (y >= z) {
                districtNav.css({ position: "fixed", "top": "0px" }),
		        districtNav.css({ width: "100%" });
            }
            else {
                districtNav.css("position", "absolute"),
                districtNav.css("top", z + "px"),
		        districtNav.css("width", "100%");
            }
        });
    });

