main.script:
function onBannerPlayerReady(event) {
  var carouselItems = document.querySelectorAll('.carousel-inner .item');
  var carouselItemsLength = carouselItems.length;
  for (var i = 0; i < carouselItemsLength; i++) {
    carouselItems[i].style.backgroundColor = 'transparent';
  } 
  /*$('.carousel-inner .item').each(function() {
      $(this).css('background-color', 'transparent');
  });*/
  var p = event.target;
  p.mute();
  p.setVolume(0);
  p.playVideo();
  p.setLoop(true);
}

