function googleTranslateElementInit() {
  new google.translate.TranslateElement({pageLanguage: 'en', layout: google.translate.TranslateElement.FloatPosition.BOTTOM_RIGHT, gaTrack: true, gaId: 'UA-7362080-1'}, 'google_translate_element');
}

body.script:
      $('#apps-navbar').click(function(event) {
        event.stopPropagation();        
        if ($('#apps-navbar .fa-caret-down').length > 0) {
          $('#apps-navbar .fa-caret-down').removeClass('fa-caret-down').addClass('fa-caret-up');
          $('#app-menu-wrap').css('display','block');
          $('.tweet-wrap').css('z-index','99');
          setTimeout(function() {
            $('#app-menu-wrap').css('opacity','1');
          }, 250);          
        } else {
          $('#apps-navbar .fa-caret-up').removeClass('fa-caret-up').addClass('fa-caret-down');
          $('#app-menu-wrap').css('display','none');  
          $('.tweet-wrap').css('z-index','101');
        }
      });  
      $(window).click(function() {
        if ($('#apps-navbar .fa-caret-up').length > 0) {
          $('#apps-navbar .fa-caret-up').removeClass('fa-caret-up').addClass('fa-caret-down');
          $('#app-menu-wrap').css('display','none');  
          $('.tweet-wrap').css('z-index','101');
        }      
      });
      $('#app-menu-wrap').click(function(event){
          event.stopPropagation();
      });
