        event.stopPropagation();      
        closeMobileMenu();  
        if ($('#apps-navbar .fa-caret-down').length > 0) {
          $('#apps-navbar .fa-caret-down').removeClass('fa-caret-down').addClass('fa-caret-up');
          $('#app-menu-wrap').css('display','block');
          $('.tweet-wrap').css('z-index','99');
          $('#apps-navbar').css('display','block');
          setTimeout(function() {
            $('#app-menu-wrap').css('opacity','1');
          }, 250);          
        } else {
          $('#apps-navbar .fa-caret-up').removeClass('fa-caret-up').addClass('fa-caret-down');
          $('#app-menu-wrap').css('display','none');  
          $('.tweet-wrap').css('z-index','101');
        }
      });  


      function openMobileMenu() {
        $('#mobile-menu').unbind('click');
        $('#mobile-menu').click(closeMobileMenu);
        $('#mobile-menu .fa').removeClass('fa-bars');
        $('#mobile-menu .fa').removeClass('fa-chevron-left');
        $('#mobile-menu .fa').addClass('fa-close');
        loadNavMenu();        
      }

      function closeMobileMenu() {
        $('#mobile-menu').unbind('click');
        $('#mobile-menu').click(openMobileMenu);
        $('#mobile-menu .fa').removeClass('fa-close');
        $('#mobile-menu .fa').addClass('fa-bars');
        hideNavMenu();        
      }      

