          $('#search51').bind('typeahead:select', function(ev, suggestion) {
            if (suggestion.initials) {
              window.location.href = "/staff-directory?staff="+suggestion.id;              
            }
            if (suggestion.text) {
              window.open('https://twitter.com/kaneland302/status/'+suggestion.id);
            }
            if (suggestion.start) {
              showCalEvent(suggestion);
            }
            if (suggestion.sponsor) {
              var actId = parseInt(suggestion.id);
              var actData = suggestion;
              if (actData) {
                $('#activity-details-modal h1').html(' '+actData.name);
                if (actData.sponsor != '') {
                  $('#activity-details-modal #sponsors-data').css('display','block');
                  $('#activity-details-modal #sponsors-data span').html(actData.sponsor);
                } else {
                  $('#activity-details-modal #sponsors-data').css('display','none');
                }
                if (actData.schedule != '') {
                  $('#activity-details-modal #meets-data').css('display','block');
                  $('#activity-details-modal #meets-data span').html(actData.schedule);
                } else {
                  $('#activity-details-modal #meets-data').css('display','none');
                }
                if (actData.location != '') {
                  $('#activity-details-modal #location-data').css('display','block');
                  $('#activity-details-modal #location-data span').html(actData.location);
                } else {
                  $('#activity-details-modal #location-data').css('display','none');
                }
                if (actData.url != '') {
                  $('#activity-details-modal #website-data').css('display','block');
                  $('#activity-details-modal #website-data span').html(actData.url);
                } else {
                  $('#activity-details-modal #website-data').css('display','none');
                }
                if (actData.url != '') {
                  $('#activity-details-modal #website-data').css('display','block');
                  $('#activity-details-modal #website-data a').attr('href', actData.url);
                  var tmpHandle = actData.url.split('/');
                  if (actData.url.indexOf('witter') != -1) {
                    $('#activity-details-modal #website-data i').css('display','inline-block');
                    $('#activity-details-modal #website-data span').html('@'+tmpHandle[tmpHandle.length - 1]);                  
                  } else {
                    $('#activity-details-modal #website-data i').css('display','none');
                    $('#activity-details-modal #website-data span').html(tmpHandle[tmpHandle.length - 1]);                  
                  }
                } else {
                  $('#activity-details-modal #website-data').css('display','none');
                }
                if (actData.description != '') {
                  $('#activity-details-modal #more-info-hs').html(actData.description);
                } else {
                  $('#activity-details-modal #more-info-hs').html('For more information please contact the High School at:  (630) 365-5100');
                }
                $('#activity-details-modal').modal('show');
              }
            } else {
              if (suggestion.url || suggestion.modal) {
                if (suggestion.modal) {
                  $(suggestion.target).modal('show');
                } else {
                  if (suggestion.url.indexOf('/new-student-registration') == -1
                      && suggestion.url.indexOf('/doc?') == -1
                      && suggestion.url.indexOf('/folder?') == -1
                      && suggestion.url.indexOf('/staff-directory') == -1
                      && suggestion.url.indexOf('/free-and-reduced-lunch-applications') == -1
                      && suggestion.url.indexOf('http') == -1) {
                    if (suggestion.target) {
                      window.open('//www.kaneland.org'+$(this).attr('data-href'), $(this).attr('data-target'));
                    } else {
                      window.location.href = '//www.kaneland.org'+suggestion.url;  
                    }                    
                  } else {
                    if (suggestion.target) {
                      window.open(suggestion.url, suggestion.target);
                    } else {
                      window.location.href = suggestion.url;  
                    }                  
                  }
                }
              }
            }
          });  

