          $('#submit-safety-form').click(function() {
              var facilities = '';
              if ($('#KHS-check').is(':checked')) {
                facilities += '-KHS';
              }
              if ($('#KHMS-check').is(':checked')) {
                facilities += '-KHMS';
              }
              if ($('#KBK-check').is(':checked')) {
                facilities += '-KBK';
              }
              if ($('#KME-check').is(':checked')) {
                facilities += '-KME';
              }  
              if ($('#KSS-check').is(':checked')) {
                facilities += '-KSS';
              }  
              if ($('#KST-check').is(':checked')) {
                facilities += '-KST';
              } 
              if ($('#D302-check').is(':checked')) {
                facilities += '-D302';
              }     
              if ($('#FVCC-check').is(':checked')) {
                facilities += '-FVCC';
              }
              if (facilities == '') {
                alert('You must select at least one facility.');
              } else {
                if ($('#tip-message').val() != '') {
                  var tipData = {"message": $('#tip-message').val(), "ip": "10.200.0.200", "facilities": facilities};
                  $.ajax({
                    type: "POST",
                    url: "/forms/safety-tip.php",
                    data: tipData,
                    success: function() {
                        $('.safety-form').each(function() {
                          $(this).css('display','none');
                        });
                        $('#submitted-safety-form').prepend('


');
                        $('#submitted-safety-form').css('display','block');
                    }
                  });                
                } else {
                  alert('You must enter a concern.');
                }  
              }          
          });

          $('.kcn-sub-btn').each(function(){
            $(this).click(function() {
                var emailSub = false;
                if ($(this).hasClass('btn-lg')) {
                  emailSub = $('#exampleInputEmail2');
                } else {
                  emailSub = $('.tab-col .input-group input');
                }
                if (validateEmail($(emailSub).val())) {
                  $.ajax({
                    type: "POST",
                    url: "/forms/kcn-sub.php",
                    data: {"email": $(emailSub).val()},
                    success: function() {}
                  });                     
                  $(emailSub).val('');
                  $('#confirmed-kcn-subscribe-modal').modal('show');
                } else {
                  alert('You must enter a valid email');
                }
            });
          });

