          window.twttr = (function(d, s, id) {
            var js, fjs = d.getElementsByTagName(s)[0],
              t = window.twttr || {};
            if (d.getElementById(id)) return t;
            js = d.createElement(s);
            js.id = id;
            js.src = "//platform.twitter.com/widgets.js";
            fjs.parentNode.insertBefore(js, fjs);

            t._e = [];
            t.ready = function(f) {
              t._e.push(f);
            };

            return t;
          }(document, "script", "twitter-wjs"));          

        });  

      function showCalEvent(calEvent) {
          $('#event-modal').modal('hide');
          $('body').removeClass('modal-open');
          $('.modal-backdrop').remove();
          var eventDetailsScript = "get-event-details";
                    $.getJSON( "/cal/"+eventDetailsScript+".php?id="+calEvent.id+"&title="+calEvent.title, function(data) {
              $('#event-modal h4').html(calEvent.title);
              if (calEvent.allDay) {
                $('#event-modal #event-modal-time').html('All Day');
              } else{
                $('#event-modal #event-modal-time').html(calEvent.time);
              }
              $('#event-modal #event-modal-day').html(calEvent.month+' '+calEvent.day);
              if (calEvent.location == '') {
                calEvent.location = 'Not available';
              }
              $('#event-modal #event-modal-loc').html(calEvent.location);
              var calImg = '';
              switch (calEvent.calendar) {
                case 'KHS':
                  calImg = '';
                break;
                case 'KHMS':
                  calImg = '';
                break;
                case 'KMS':
                  calImg = '';
                break;                
                case 'KSS':
                  calImg = '';
                break;
                case 'KST':
                  calImg = '';
                break;
                case 'KME':
                  calImg = '';
                break;
                case 'KBK':
                  calImg = '';
                break;                
              }
              $('#event-modal #event-modal-cal').html(calImg+' '+calEvent.calendar);
              $('#event-modal .similar-events-launch').html('');
              if (data.similar_events) {
                if (data.similar_events.length > 0) {
                  var eventsTxt = 'Events';
                  if (data.similar_events.length == 1) {
                    eventsTxt = 'Event';
                  }
                  $('#event-modal .similar-events-launch').append(''+data.similar_events.length+' Similar '+eventsTxt+'');
                  $.each(data.similar_events, function(index, simEv) {
                    var newDiv = $("

"+simEv.month+"
"+simEv.day+"");
                    newDiv.addClass('similar-cal-day');
                    newDiv.click(function() {showCalEvent(simEv)}); 
                    $('#event-modal .similar-events-launch').append(newDiv);
                  });
                }
              }
              setTimeout(function(){$('#event-modal').modal('show')}, 500);
          });          
      }

      function resizeAppBar() {
        if ($(window).width() < 769) {
          $('.apps-section').addClass('double-size-apps');
        } else {
          $('.apps-section').removeClass('double-size-apps');
        }
        $('.apps-section .app-icons').css('opacity','1');
      }

      function resizeStaticBanner() {
        if ($(window).width() < 551) {
          $('#static-banner h1').css('width', ($(window).width() - 30) + 'px');          
        } else {
          $('#static-banner h1').css('width', '100%');          
        }
      }

      function resizeTwitterGrid() {
        if ($(window).width() >= 960) {
          var blockCount = -2;
          $('.twitter-grid-block').each(function() {
            $(this).css('width','33.3%');
            blockCount++;
          });
          if (blockCount % 3 == 1) {
            $('#filler-block-1').css('display','inline-block');
            $('#filler-block-2').css('display','inline-block');            
          } else if (blockCount % 3 == 2) {
            if (Math.random() >= 0.5) {
              $('#filler-block-1').css('display','none');
              $('#filler-block-2').css('display','inline-block');
            } else {
              $('#filler-block-1').css('display','inline-block');
              $('#filler-block-2').css('display','none');
            }
          } else {
            $('#filler-block-1').css('display','none');
            $('#filler-block-2').css('display','none');
          }
        } else if ($(window).width() >= 660) {
          var blockCount = -2;
          $('.twitter-grid-block').each(function() {
            $(this).css('width','50%');
            blockCount++;
          });
          if (blockCount % 2 == 1) {
            if (Math.random() >= 0.5) {
              $('#filler-block-1').css('display','none');
              $('#filler-block-2').css('display','inline-block');
            } else {
              $('#filler-block-1').css('display','inline-block');
              $('#filler-block-2').css('display','none');
            }
          } else {
            $('#filler-block-1').css('display','none');
            $('#filler-block-2').css('display','none');
          }
        } else {
          $('.twitter-grid-block').each(function() {
            $(this).css('width','100%');
          });          
          $('#filler-block-1').css('display','none');
          $('#filler-block-2').css('display','none');          
        } 
      }

      function resizeNavMenu() {
        if (!mobileSearchLoaded) {
          if ($('body').hasClass('calendar') || $('body').hasClass('calendar-mobile') || $('body').hasClass('staff-directory')) {
            $('#nav-menu').css('height', ($(window).height() - 50) + 'px');
          } else {
            $('#nav-menu').css('height', ($(window).height() - 80) + 'px');
          }

          $('#nav-menu').css('height', ($(window).height() - 50) + 'px');
          if ($(window).width() < 769 && openNavTab) {
            setupViewForMobileNavTab(openNavTab);
          } else if ($(window).width() > 768 && openNavTab) {
            destroyViewForMobileNavTab(openNavTab);
            $('#nav-menu').css('display','none');
          } else if ($(window).width() > 768) {
                        $('body').css('height', 'auto');
            $('body').css('overflow', 'auto');          

            $('#nav-menu').css('display','none');
          }
          if ($(window).width() < 769) {
            $('.topnav > div').each(function() {$(this).css('display','none')});
            $('#mobile-nav').css('display','block');
                      } else {
            $('.topnav > div').each(function() {$(this).css('display','block')});
            $('#mobile-nav').css('display','none');
          }
          if ($(window).width() < 420) {
            $('body').css('width', $(window).width() + 'px');
          } else {
            $('body').css('width', '100%');
          }
        }
        $('.carousel-inner .item .container').each(function() {
          $(this).css('width', $(window).width());
        })
      }

      function loadMobileSearch(e) {
        mobileSearchLoaded = true;
        $('.search-wrap .fa-search').unbind('click');
        $('.search-wrap').fadeOut();
        $('.topnav > div').each(function(){
            if (!$(this).hasClass('search-wrap')) {
                $(this).fadeOut();
            }
        }); 
        if ($('#mobile-nav').css('display') == 'block') {
          $('#mobile-nav').fadeOut();          
        }
        setTimeout(function() {
          $('.search-wrap').addClass('mobile-search-bar');
          $('.search-wrap').fadeIn();
          $('#search51').focus();
          $('#main').css('display','none');
          $('#footer').css('display','none');
          $('body').css('background','#111');
        }, 500);
      }

      function closeMobileSearch() {
        mobileSearchLoaded = false;
        $('.search-wrap').fadeOut();
        $('.search-wrap .icon.icon-close').css('display','none');
        setTimeout(function() {
          if ($('body').hasClass('doc-body')) {
            $('.search-wrap').removeClass('mobile-search-bar');
            if ($(window).width() > 1399) {
              $('body').css('background','#f5f5f5');
            } else {
              $('body').css('background','#f5f5f5');
            }
            $('.search-wrap .fa-search').click(loadMobileSearch);
            $('.topnav > div').each(function(){
              $(this).fadeIn();
            }); 
          } else {
            $('.search-wrap').removeClass('mobile-search-bar');
            $('#main').css('display','block');
            $('#footer').css('display','block');
            if ($(window).width() > 1399) {
              $('body').css('background','#f5f5f5');
            } else {
              $('body').css('background','#f5f5f5');
            }
            $('.search-wrap .fa-search').click(loadMobileSearch);
            if ($('body').hasClass('doc-folder')) {
              $('.topnav > div').each(function(){
                $(this).fadeIn();
              }); 
            } else {
              if ($(window).width() > 768) {
                $('.topnav > div').each(function(){
                  $(this).fadeIn();
                }); 
              } else {
                $('#mobile-nav').fadeIn();   
                $('.topnav > div').each(function(){
                  $(this).css('display','none');
                }); 
              }
            }
          }
        }, 500);        
      }         



